C                                                                               
C     FUNCTION SUMMATION                                                        
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      REAL*8 FUNCTION DSUM(D,N)                                                 
      REAL*8 D(N)                                                               
C                                                                               
C                                                                               
C                                                                               
      DSUM=0.0D+00                                                              
      DO 100 I=1,N                                                              
      DSUM=DSUM+D(I)                                                            
  100 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
