C                                                                               
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
C     RRGION                                                                    
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      SUBROUTINE RRGION(NR,DTH,DBETA)                                           
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 BETAK,BETAL                                                        
C                                                                               
C                                                                               
C                                                                               
      DATA DTHT,DTH1,DTH2,DTH3                                                  
     * / 4.219990731D-01 ,  9.626911787D-01 ,  1.333462073D+00 ,                
     *   1.657886606D+00 /                                                      
C                                                                               
      DATA DBETAT,DBETA1,DBETA2                                                 
     * / 2.763311032D-05 ,  7.475191707D-01 ,  4.520795660D+00 /                
C                                                                               
      DATA DVC1,DTC1,DPC1                                                       
     * / 3.17      D-03 ,  6.473     D+02 , 2.212      D+07 /                   
C                                                                               
C                                                                               
C                                                                               
      DT=DTH*DTC1                                                               
      DP=DBETA*DPC1                                                             
C                                                                               
C                                                                               
C                                                                               
      IF(DTH.GE.DTHT) GO TO 100                                                 
      WRITE(6,6000) DT                                                          
 6000 FORMAT(///,10X,'THE TEMPERATURE IS LESS THAN 273.16 K (0 C)',             
     *         /,10X,'TEMPERATURE = ',1PD12.5,///)                              
      STOP                                                                      
C                                                                               
C                                                                               
C                                                                               
  100 CONTINUE                                                                  
      IF(DTH.LE.DTH3) GO TO 110                                                 
      WRITE(6,6100) DT                                                          
 6100 FORMAT(///,10X,'THE TEMPERATURE IS HIGHER THAN 1073.15 K',                
     *               ' (800 C)',/,10X,'TEMPERATURE = ',1PE12.5,//)              
      STOP                                                                      
C                                                                               
C                                                                               
C                                                                               
  110 CONTINUE                                                                  
      IF(DBETA.GE.0.0D+00) GO TO 120                                            
      WRITE(6,6200) DP                                                          
 6200 FORMAT(///,10X,'THE PRESSURE IS NEGATIVE',//,                             
     *           10X,'PRESSURE = ',1PD12.5,' K ',//)                            
      STOP                                                                      
C                                                                               
C                                                                               
C                                                                               
  120 CONTINUE                                                                  
      IF(DBETA.LE.DBETA2) GO TO 130                                             
      WRITE(6,6300) DP                                                          
 6300 FORMAT(///,10X,'THE PRESSURE IS HIGHER THAN 10E8 N/M2',//,                
     *           10X,'PRESSURE = ',1PD12.5,' N/M2',//)                          
      STOP                                                                      
C                                                                               
C                                                                               
C                                                                               
  130 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      IF(DTH.LE.DTH1) GO TO 200                                                 
      IF(DTH.LT.1.0D+00) GO TO 300                                              
      IF(DTH.LT.DTH2) GO TO 400                                                 
C                                                                               
C                                                                               
C                                                                               
      NR=2                                                                      
      GO TO 500                                                                 
C                                                                               
C                                                                               
C                                                                               
  200 CONTINUE                                                                  
      DBKTH=BETAK(DTH)                                                          
      IF(DBETA.GE.DBKTH) GO TO 210                                              
      NR=2                                                                      
      GO TO 500                                                                 
  210 CONTINUE                                                                  
      IF(DBETA.NE.DBKTH) GO TO 220                                              
      NR=6                                                                      
      GO TO 500                                                                 
  220 CONTINUE                                                                  
      NR=1                                                                      
      GO TO 500                                                                 
C                                                                               
C                                                                               
C                                                                               
  300 CONTINUE                                                                  
      DBKTH=BETAK(DTH)                                                          
      DBLTH=BETAL(DTH)                                                          
      IF(DBETA.GT.DBLTH) GO TO 310                                              
      NR=2                                                                      
      GO TO 500                                                                 
  310 CONTINUE                                                                  
      IF(DBETA.GE.DBKTH) GO TO 320                                              
      NR=3                                                                      
      GO TO 500                                                                 
  320 CONTINUE                                                                  
      IF(DBETA.NE.DBKTH) GO TO 330                                              
      NR=5                                                                      
      GO TO 500                                                                 
  330 CONTINUE                                                                  
      NR=4                                                                      
      GO TO 500                                                                 
C                                                                               
C                                                                               
C                                                                               
  400 CONTINUE                                                                  
      DBLTH=BETAL(DTH)                                                          
      IF(DBETA.GT.DBLTH) GO TO 410                                              
      NR=2                                                                      
      GO TO 500                                                                 
  410 CONTINUE                                                                  
      NR=3                                                                      
C                                                                               
C                                                                               
C                                                                               
  500 CONTINUE                                                                  
      RETURN                                                                    
      END                                                                       
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
C     REGION                                                                    
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      SUBROUTINE REGION(NR,T,P)                                                 
      IMPLICIT REAL*8 (D)                                                       
C                                                                               
C                                                                               
C                                                                               
      DATA DVC1,DTC1,DPC1                                                       
     * / 3.17      D-03 ,  6.473     D+02 , 2.212      D+07 /                   
C                                                                               
C                                                                               
C                                                                               
      DTH=DBLE(T)/DTC1                                                          
      DBETA=DBLE(P)/DPC1                                                        
C                                                                               
C                                                                               
C                                                                               
      CALL RRGION(NR,DTH,DBETA)                                                 
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
