      REAL*8 FUNCTION    GZAYA(DTH,DBETA)                                       
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 A(23),SA(12)                                                       
      DIMENSION DNTH(20),DBET(4),D(8)                                           
C                                                                               
C     LARGE A PARAMETERS                                                        
C                                                                               
      DATA (A(I),I=1,23)                                                        
     * /  6.824687741D+03 , -5.422063673D+02 , -2.096666205D+04 ,               
     *    3.941286787D+04 , -6.733277739D+04 ,  9.902381028D+04 ,               
     *   -1.093911774D+05 ,  8.590841667D+04 , -4.511168742D+04 ,               
     *    1.418138926D+04 , -2.017271113D+03 ,  7.982692717D+00 ,               
     *   -2.616571843D-02 ,  1.522411790D-03 ,  2.284279054D-02 ,               
     *    2.421647003D+02 ,  1.269716088D-10 ,  2.074838328D-07 ,               
     *    2.174020350D-08 ,  1.105710498D-09 ,  1.293441934D+01 ,               
     *    1.308119072D-05 ,  6.047626338D-14 /                                  
C                                                                               
C     SMALL A PARAMETERS                                                        
C                                                                               
      DATA (SA(I),I=1,12)                                                       
     * /  8.438375405D-01 ,  5.362162162D-04 ,  1.72       D+00 ,               
     *    7.342278489D-02 ,  4.975858870D-02 ,  6.5371543  D-01 ,               
     *    1.150      D-06 ,  1.5108     D-05 ,  1.41880    D-01 ,               
     *    7.002753165D+00 ,  2.995284926D-04 ,  2.040      D-01 /               
C                                                                               
C                                                                               
C                                                                               
      DTEMP=DTH                                                                 
      DT1=DTEMP                                                                 
      DO 10 I=1,20                                                              
      DNTH(I)=DT1                                                               
      DT1=DT1*DTEMP                                                             
   10 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      DTEMP=DBETA                                                               
      DT1=DTEMP                                                                 
      DO 20 I=1,4                                                               
      DBET(I)=DT1                                                               
      DT1=DT1*DTEMP                                                             
   20 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      DY=1.0D+00-SA(1)*DNTH(2)-SA(2)/DNTH(6)                                    
      DZ=DY+DSQRT(SA(3)*DY**2-2.0D+00*SA(4)*DNTH(1)                             
     *                       +2.0D+00*SA(5)*DBET(1))                            
C                                                                               
C                                                                               
C                                                                               
      D(1)=A(1)*DNTH(1)*(1.0D+00-DLOG(DNTH(1)))                                 
C                                                                               
C                                                                               
C                                                                               
      D(2)=A(2)                                                                 
      DO 100 I=2,10                                                             
      D(2)=D(2)+A(I+1)*DNTH(I-1)                                                
  100 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(3)=A(12)*17.0D+00*(DZ/29.0D+00-DY/12.0D+00)*DZ**(12./17.)               
C                                                                               
C                                                                               
C                                                                               
      D(4)=(A(13)+A(14)*DNTH(1)+A(15)*DNTH(2)                                   
     *     +A(16)*(SA(6)-DNTH(1))**10                                           
     *     +A(17)/(SA(7)+DNTH(19)))*DBET(1)                                     
C                                                                               
C                                                                               
C                                                                               
      D(5)=-(A(18)*DBET(1)+A(19)*DBET(2)+A(20)*DBET(3))                         
     *     /(SA(8)+DNTH(11))                                                    
C                                                                               
C                                                                               
C                                                                               
      D(6)=-A(21)*DNTH(18)*(SA(9)+DNTH(2))                                      
     *     *(1.0D+00/(SA(10)+DNTH(1))**3+SA(11)*DNTH(1))                        
C                                                                               
C                                                                               
C                                                                               
      D(7)=A(22)*(SA(12)-DNTH(1))*DBET(3)                                       
C                                                                               
C                                                                               
C                                                                               
      D(8)=A(23)*DBET(4)/DNTH(20)                                               
C                                                                               
C                                                                               
C                                                                               
      GZAYA=DSUM(D,8)                                                           
      RETURN                                                                    
      END                                                                       
