C                                                                               
      D(1)=A(1)*DTH+A(2)                                                        
      DO 100 I=3,10                                                             
      D(1)=D(1)-DFLOAT(I-2)*A(I+1)*DTH**(I-1)                                   
  100 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(2)=A(12)*(DZ*(17.0D+00*(DZ/29.0D+00-DY/12.0D+00)                        
     *     +5.0D+00*DTH*DYD/12.0D+00)+SA(4)*DTH-(SA(3)-1.0D+00)                 
     *     *DTH*DY*DYD)/DZ**(5./17.)                                            
C                                                                               
C                                                                               
C                                                                               
      D(3)=(A(13)-A(15)*DTH**2+A(16)*(9.0D+00*DTH+SA(6))                        
     *     *(SA(6)-DTH)**9+A(17)*(20.0D+00*DTH**19+SA(7))                       
     *     /(SA(7)+DTH**19)**2)*DBETA                                           
C                                                                               
C                                                                               
C                                                                               
      D(4)=-(12.0D+00*DTH**11+SA(8))/(SA(8)+DTH**11)**2                         
     *     *(A(18)*DBETA+A(19)*DBETA**2+A(20)*DBETA**3)                         
C                                                                               
C                                                                               
C                                                                               
      D(5)=A(21)*DTH**18*(17.0D+00*SA(9)+19.0D+00*DTH**2)                       
     *     *(1.0D+00/(SA(10)+DBETA)**3+SA(11)*DBETA)                            
C                                                                               
C                                                                               
C                                                                               
      D(6)=A(22)*SA(12)*DBETA**3                                                
C                                                                               
C                                                                               
C                                                                               
      D(7)=21.0D+00*A(23)/DTH**20*DBETA**4                                      
C                                                                               
C                                                                               
C                                                                               
      EPSI1=DSUM(D,7)                                                           
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
