      DTEMP=DT                                                                  
      DO 20 I=1,6                                                               
      DB(I)=DT                                                                  
      DT=DT*DTEMP                                                               
   20 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(1)=-ALF1-DI1*DLOG(DBETA)+B0(1)*DLOG(DTH)                                
      DO 100 I=2,5                                                              
      D(1)=D(1)-DFLOAT(I-1)*B0(I+1)*DTH**(I-2)                                  
  100 CONTINUE                                                                  
C                                                                               
