C                                                                               
C     REDUCED FREE ENERGY (HELMHOLTZ FUNCTION)                                  
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
      REAL*8 FUNCTION PHYC(DTH,DX)                                              
      IMPLICIT REAL*8 (D)                                                       
      DIMENSION  DXN(10),DTH1(9),D(9)                                           
      REAL*8 C0(13),C1(7),C2(8),C3(10),C6(5),C7(9),                             
     *       C40,C41,C50                                                        
C                                                                               
C                                                                               
C     C PARAMETER                                                               
C                                                                               
      DATA (C0(I),I=1,13)                                                       
     * / -6.8399     D+00 , -1.72260420 D-02 , -7.771750390D+00 ,               
     *    4.204607520D+00 , -2.768070380D+00 ,  2.104197070D+00 ,               
     *  -1.146495880D+00 ,   2.231380850D-01 ,  1.162503630D-01 ,               
     *  -8.209005440D-02 ,   1.941292390D-02 , -1.694705760D-03 ,               
     *  -4.311577033D+00 /                                                      
      DATA (C1(I),I=1,7)                                                        
     * /  7.086360850D-01 ,  1.236794550D+01 , -1.203890040D+01 ,               
     *    5.404374220D+00 , -9.938650430D-01 ,  6.275231820D-02 ,               
     *   -7.747430160D+00 /                                                     
      DATA (C2(I),I=1,8)                                                        
     * / -4.298850920D+00 ,  4.314305380D+01 , -1.416193130D+01 ,               
     *    4.041724590D+00 ,  1.555463260D+00 , -1.665689350D+00 ,               
     *    3.248811580D-01 ,  2.936553250D+01 /                                  
      DATA (C3(I),I=1,10)                                                       
     * /  7.948418420D-06 ,  8.088597470D+01 , -8.36153380 D+01 ,               
     *    3.586365170D+01 ,  7.518959540D+00 , -1.26160640 D+01 ,               
     *    1.097174620D+00 ,  2.121454920D+00 , -5.465295660D-01 ,               
     *    8.328754130D+00 /                                                     
      DATA C40,C41,C50                                                          
     * /  2.759717760D-06 , -5.090739850D-04 ,  2.106363320D+02 /               
      DATA (C6(I),I=1,5)                                                        
     * /  5.528935335D-02 , -2.336365955D-01 ,  3.697071420D-01 ,               
     *   -2.596415470D-01 ,  6.828087013D-02 /                                  
      DATA (C7(I),I=1,9)                                                        
     * / -2.571600553D+02 , -1.518783715D+02 ,  2.220723208D+01 ,               
     *   -1.802039570D+02 ,  2.357096220D+03 , -1.462335698D+04 ,               
     *    4.542916630D+04 , -7.053556432D+04 ,  4.381571428D+04 /               
C                                                                               
C                                                                               
C                                                                               
      DT=DTH-1.0D+00                                                            
      DTEMP=DT                                                                  
      DO 10 I=1,9                                                               
      DTH1(I)=DT                                                                
      DT=DT*DTEMP                                                               
   10 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      DT=DX                                                                     
      DT=DTEMP                                                                  
      DO 20 I=1,10                                                              
      DXN(I)=DT                                                                 
      DT=DT*DTEMP                                                               
   20 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(1)=C0(1)                                                                
C                                                                               
      D(2)=C0(2)*DX                                                             
C                                                                               
      DT=0.0D+00                                                                
      DO 100 I=2,11                                                             
      DT=DT+C0(I+1)/DXN(I-1)                                                    
  100 CONTINUE                                                                  
      D(3)=DT+C0(13)*DLOG(DX)                                                   
C                                                                               
      DT=C1(1)*DX+C1(7)*DLOG(DX)                                                
      DO 110 I=2,6                                                              
      DT=DT+C1(I)/DXN(I-1)                                                      
  110 CONTINUE                                                                  
      DT=DT*DTH1(1)                                                             
      D(4)=DT                                                                   
C                                                                               
      DT=C2(1)*DX+C2(8)*DLOG(DX)                                                
      DO 130 I=2,7                                                              
      DT=DT+C2(I)/DXN(I-1)                                                      
  130 CONTINUE                                                                  
      DT=DT*DTH1(2)                                                             
      D(5)=DT                                                                   
C                                                                               
      DT=C3(1)*DX+C3(10)*DLOG(DX)                                               
      DO 140 I=2,9                                                              
      DT=DT+C3(I)/DXN(I-1)                                                      
  140 CONTINUE                                                                  
      DT=DT*DTH1(3)                                                             
      D(6)=DT                                                                   
C                                                                               
      DT=(C40+C41/DXN(5))*DTH1(1)/DTH**23+C50*DTH*DLOG(DTH)                     
      D(7)=DT                                                                   
C                                                                               
      DT=0.0D+00                                                                
      DO 150 I=1,5                                                              
      DT=DT+C6(I)/DTH**(-1+I)                                                   
  150 CONTINUE                                                                  
      DT=DT*DXN(6)                                                              
      D(8)=DT                                                                   
C                                                                               
      DT=0.0D+00                                                                
      DO 160 I=1,9                                                              
      DT=DT+C7(I)*DTH1(I)                                                       
  160 CONTINUE                                                                  
      D(9)=DT                                                                   
C                                                                               
C                                                                               
C                                                                               
      PHYC=DSUM(D,9)                                                            
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
