C                                                                               
C     FUNCTION VKAY-3                                                           
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
      REAL*8 FUNCTION VKAY3(DTH,DBETA)                                          
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 VKAY2,XBETA3,BETAL                                                 
      COMMON /COMPAR/ DTHC , DBETAC                                             
C                                                                               
C                                                                               
C                                                                               
      EXTERNAL XBETA3                                                           
C                                                                               
C                                                                               
C                                                                               
      DTHC=DTH                                                                  
      DBETAC=DBETA                                                              
C                                                                               
C                                                                               
      DBL=BETAL(DTH)                                                            
      DSV2=VKAY2(DTH,DBL)                                                       
      DFXL=XBETA3(DSV2)                                                         
C                                                                               
C                                                                               
C                                                                               
         IT=0                                                                   
  200    IT=IT+1                                                                
         IF(IT.LE.100) GO TO 300                                                
         WRITE(6,6100) DTH,DBETA                                                
 6100    FORMAT(////,10X,'VKAY3 : IT IS LARGER THAN 100',                       
     *             /,10X,'DTH = ',1PE12.5,                                      
     *             /,10X,'BETA = ',1PE12.5,//)                                  
         STOP                                                                   
C                                                                               
C                                                                               
C                                                                               
  300 CONTINUE                                                                  
      DSV1=DSV2/1.1**(IT)                                                       
      DFXR=XBETA3(DSV1)                                                         
      IF(DFXL*DFXR.GE.0.0D+00) GO TO 200                                        
      DSV2=DSV1*1.1                                                             
C                                                                               
C                                                                               
C                                                                               
      CALL DNLBIT(DSV,XBETA3,DSV1,DSV2,1.0D-12,100,IER)                         
      IF(IER.EQ.0) GO TO 100                                                    
      WRITE(6,6000) IER,DTH,DBETA                                               
 6000 FORMAT(///10X,'VKAY3.NLBIT IER CONDITION = ',I3,                          
     *         /10X,'TH = ',1PD12.5,                                            
     *         /10X,'BETA = ',1PD12.5,//)                                       
      STOP                                                                      
  100 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      VKAY3=DSV                                                                 
C                                                                               
      RETURN                                                                    
      END                                                                       
