      D(4)=-(C3(1)+C3(10)/DXN(2))                                               
      DO 250 I=2,9                                                              
      D(4)=D(4)-DFLOAT(1-I)*C3(I)/DXN(I+1)                                      
  250 CONTINUE                                                                  
      D(4)=D(4)*DTH1**3                                                         
