     *    5.404374220D+00 , -9.938650430D-01 ,  6.275231820D-02 ,               
     *   -7.747430160D+00 /                                                     
      DATA (C2(I),I=1,8)                                                        
     * / -4.298850920D+00 ,  4.314305380D+01 , -1.416193130D+01 ,               
     *    4.041724590D+00 ,  1.555463260D+00 , -1.665689350D+00 ,               
     *    3.248811580D-01 ,  2.936553250D+01 /                                  
      DATA (C3(I),I=1,10)                                                       
