C                                                                               
C                                                                               
      DT=DTH-1.0D+00                                                            
      DT1=DT                                                                    
      DTH1(1)=1.0D+00                                                           
      DO 125 I=1,8                                                              
      DTH1(I+1)=DT                                                              
      DT=DT*DT1                                                                 
  125 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(1)=-ALF1-(C1(1)*DXN(2)+C1(7)*DLOG(DXN(2))+C50)                          
      DO 150 I=2,6                                                              
      D(1)=D(1)-C1(I)/DXN(I)                                                    
  150 CONTINUE                                                                  
C                                                                               
      D(2)=-(C2(1)*DXN(2)+C2(8)*DLOG(DXN(2)))                                   
      DO 160 I=2,7                                                              
      D(2)=D(2)-C2(I)/DXN(I)                                                    
  160 CONTINUE                                                                  
      D(2)=D(2)*2.0D+00*DTH1(2)                                                 
C                                                                               
C                                                                               
C                                                                               
      D(3)=-(C3(1)*DXN(2)+C3(10)*DLOG(DXN(2)))                                  
      DO 200 I=2,9                                                              
      D(3)=D(3)-C3(I)/DXN(I)                                                    
  200 CONTINUE                                                                  
      D(3)=D(3)*3.0D+00*DTH1(3)                                                 
C                                                                               
C                                                                               
C                                                                               
      D(4)=(C40+C41/DXN(6))*(22.0D+00/DTH**23-23.0D+00/DTH**24)                 
     *     -C50*DLOG(DTH)                                                       
C                                                                               
C                                                                               
C                                                                               
      D(5)=0.0D+00                                                              
      DO 250 I=1,5                                                              
      D(5)=D(5)+DFLOAT(I+1)*C6(I)/DTH**(2+I)                                    
