     *  -8.209005440D-02 ,   1.941292390D-02 , -1.694705760D-03 ,               
     *  -4.311577033D+00 /                                                      
      DATA (C1(I),I=1,7)                                                        
     * /  7.086360850D-01 ,  1.236794550D+01 , -1.203890040D+01 ,               
     *    5.404374220D+00 , -9.938650430D-01 ,  6.275231820D-02 ,               
     *   -7.747430160D+00 /                                                     
      DATA (C2(I),I=1,8)                                                        
     * / -4.298850920D+00 ,  4.314305380D+01 , -1.416193130D+01 ,               
     *    4.041724590D+00 ,  1.555463260D+00 , -1.665689350D+00 ,               
     *    3.248811580D-01 ,  2.936553250D+01 /                                  
      DATA (C3(I),I=1,10)                                                       
     * /  7.948418420D-06 ,  8.088597470D+01 , -8.36153380 D+01 ,               
     *    3.586365170D+01 ,  7.518959540D+00 , -1.26160640 D+01 ,               
     *    1.097174620D+00 ,  2.121454920D+00 , -5.465295660D-01 ,               
     *    8.328754130D+00 /                                                     
      DATA C40,C41,C50                                                          
     * /  2.759717760D-06 , -5.090739850D-04 ,  2.106363320D+02 /               
      DATA (C6(I),I=1,5)                                                        
     * /  5.528935335D-02 , -2.336365955D-01 ,  3.697071420D-01 ,               
     *   -2.596415470D-01 ,  6.828087013D-02 /                                  
      DATA (C7(I),I=1,9)                                                        
     * / -2.571600553D+02 , -1.518783715D+02 ,  2.220723208D+01 ,               
     *   -1.802039570D+02 ,  2.357096220D+03 , -1.462335698D+04 ,               
     *    4.542916630D+04 , -7.053556432D+04 ,  4.381571428D+04 /               
C                                                                               
C                                                                               
C                                                                               
      DATA ALF0 / 0.0        D+00 /                                             
C                                                                               
C                                                                               
C                                                                               
      DT=DX                                                                     
      DT1=DT                                                                    
      DXN(1)=1.0D+00                                                            
      DO 100 I=1,10                                                             
      DXN(I+1)=DT                                                               
      DT=DT*DT1                                                                 
  100 CONTINUE                                                                  
C                                                                               
      DT=DTH-1.0D+00                                                            
      DT1=DT                                                                    
      DTH1(1)=1.0D+00                                                           
      DO 150 I=1,8                                                              
      DTH1(I+1)=DT                                                              
      DT=DT*DT1                                                                 
  150 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(1)=ALF0+C0(1)-C0(13)-C50-C1(1)*DX+(C0(13)-C1(7))*DLOG(DX)               
      DO 200 I=2,11                                                             
      D(1)=D(1)+DFLOAT(I)*C0(I+1)/DXN(I)                                        
  200 CONTINUE                                                                  
      DO 250 I=2,6                                                              
      D(1)=D(1)-C1(I)/DXN(I)                                                    
  250 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(2)=(-C1(7)-C50)-(C1(1)+2.0D+00*C2(1))*DX                                
     *      -2.0D+00*C2(8)*DLOG(DX)                                             
      DO 300 I=2,6                                                              
      D(2)=D(2)+DFLOAT(I-1)*C1(I)/DXN(I)                                        
  300 CONTINUE                                                                  
      DO 350 I=2,7                                                              
      D(2)=D(2)-2.0D+00*C2(I)/DXN(I)                                            
  350 CONTINUE                                                                  
      D(2)=D(2)*DTH1(2)                                                         
C                                                                               
C                                                                               
C                                                                               
      D(3)=-C2(8)-(2.0D+00*C2(1)+3.0D+00*C3(1))*DX                              
     *     -(C2(8)+3.0D+00*C3(10))*DLOG(DX)                                     
      DO 400 I=2,7                                                              
      D(3)=D(3)+DFLOAT(I-2)*C2(I)/DXN(I)                                        
  400 CONTINUE                                                                  
      DO 450 I=2,9                                                              
      D(3)=D(3)-3.0D+00*C3(I)/DXN(I)                                            
  450 CONTINUE                                                                  
      D(3)=D(3)*DTH1(3)                                                         
C                                                                               
C                                                                               
C                                                                               
      D(4)=-C3(10)-3.0D+00*C3(1)*DX-2.0D+00*C3(10)*DLOG(DX)                     
      DO 500 I=2,9                                                              
      D(4)=D(4)+DFLOAT(I-3)*C3(I)/DXN(I)                                        
  500 CONTINUE                                                                  
      D(4)=D(4)*DTH1(4)                                                         
C                                                                               
C                                                                               
C                                                                               
      D(5)=(23.0D+00*C40+28.0D+00*C41/DXN(6))/DTH**22                           
     *     -(24.0D+00*C40+29.0D+00*C41/DXN(6))/DTH**23                          
      DO 600 I=1,5                                                              
      D(5)=D(5)+DFLOAT(I-4)*C6(I)/DTH**(1+I)*DXN(7)                             
  600 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(6)=0.0D+00                                                              
      DO 700 I=1,9                                                              
      D(6)=D(6)-C7(I)*(1.0D+00+DFLOAT(I-1)*DTH)*DTH1(I)                         
  700 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      EPSI3=DSUM(D,6)                                                           
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
