C                                                                               
C                                                                               
C     D PARAMETERS                                                              
C                                                                               
      DATA ((D(I,J),J=1,5),I=3,5)                                               
     * / -1.717616747D+00 ,  3.526389875D+00 , -2.690899373D+00 ,               
     *    9.070982605D-01 , -1.138791156D-01 ,                                  
     *    1.301023613D+00 , -2.642777743D+00 ,  1.996765362D+00 ,               
     *   -6.661557013D-01 ,  8.270860589D-02 ,                                  
     *    3.426663535D-04 , -1.236521258D-03 ,  1.155018309D-03 ,               
     *    0.0        D+00 ,  0.0        D+00 /                                  
