C                                                                               
C     FUNCTION VKAY-4                                                           
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      REAL*8 FUNCTION VKAY4(DTH,DBETA)                                          
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 VKAY1,XBETA4                                                       
      COMMON /COMPAR/ DTHC , DBETAC                                             
C                                                                               
C                                                                               
C                                                                               
      DATA DTH1 / 9.626911787D-01 /                                             
C                                                                               
C                                                                               
C                                                                               
      EXTERNAL XBETA4                                                           
C                                                                               
C                                                                               
C                                                                               
      DTHC=DTH                                                                  
      DBETAC=DBETA                                                              
C                                                                               
C                                                                               
C                                                                               
      DSV1=VKAY1(DTH1,DBETA)                                                    
      DFXL=XBETA4(DSV1)                                                         
C                                                                               
C                                                                               
C                                                                               
      DFACT=1.01D+00                                                            
      IF (DFXL .GT. 0.0D+00)  DFACT=0.99D+00                                    
C                                                                               
C                                                                               
C                                                                               
      DSV2=DSV1                                                                 
C                                                                               
C                                                                               
C                                                                               
      DO 200 IT=1,100                                                           
      DSV2=DSV2*DFACT                                                           
      DFXR=XBETA4(DSV2)                                                         
      IF(DFXL*DFXR.LE.0.0D+00) GO TO 500                                        
  200 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      WRITE(6,6100) DTH,DBETA                                                   
 6100 FORMAT(///10X,'VKAY4. IT IS LARGER THAN 100',                             
     *         /10X,'TH = ',1PE12.5,                                            
     *         /10X,'BETA = ',1PE12.5,//)                                       
      STOP                                                                      
C                                                                               
C                                                                               
C                                                                               
  500 CONTINUE                                                                  
      IF(IT .GT. 1) DSV1=DSV2/DFACT                                             
C                                                                               
C                                                                               
C                                                                               
      CALL DNLBIT(DSV,XBETA4,DSV1,DSV2,1.0D-12,100,IER)                         
      IF(IER.EQ.0) GO TO 100                                                    
      WRITE(6,6000) IER,DTH,DBETA                                               
 6000 FORMAT(///10X,'VKAY4.DNLBIT IER CONDITION = ',I3,                         
     *         /10X,'TH = ',1PE12.5,                                            
     *         /10X,'BETA = ',1PE12.5,//)                                       
      STOP                                                                      
  100 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      VKAY4=DSV                                                                 
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
