C                                                                               
C     FUNCTION SGMA-4                                                           
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
C                                                                               
      REAL*8 FUNCTION SGMA4(DTH,DX)                                             
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 SGMA3                                                              
      DIMENSION D(5,5)                                                          
C                                                                               
C                                                                               
C     D PARAMETERS                                                              
C                                                                               
      DATA ((D(I,J),J=1,5),I=3,5)                                               
     * / -1.717616747D+00 ,  3.526389875D+00 , -2.690899373D+00 ,               
     *    9.070982605D-01 , -1.138791156D-01 ,                                  
     *    1.301023613D+00 , -2.642777743D+00 ,  1.996765362D+00 ,               
     *   -6.661557013D-01 ,  8.270860589D-02 ,                                  
     *    3.426663535D-04 , -1.236521258D-03 ,  1.155018309D-03 ,               
     *    0.0        D+00 ,  0.0        D+00 /                                  
C                                                                               
C                                                                               
C                                                                               
      DATA DTH1 / 9.626911787D-01 /                                             
C                                                                               
C                                                                               
C                                                                               
      DY=(1.0D+00-DTH)/(1.0D+00-DTH1)                                           
C                                                                               
C                                                                               
      DT=32.0D+00*DY**31*(D(5,1)+D(5,2)*DX+D(5,3)*DX**2)                        
C                                                                               
C                                                                               
      DO 200 I=3,4                                                              
      DO 200 J=1,5                                                              
      DT=DT+DFLOAT(I)*D(I,J)*DY**(I-1)/DX**(J-1)                                
  200 CONTINUE                                                                  
C                                                                               
      DT=DT/(1.0D+00-DTH1)                                                      
C                                                                               
      DSG3=SGMA3(DTH,DX)                                                        
      DT=DSG3+DT                                                                
C                                                                               
      SGMA4=DT                                                                  
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
