C     R. TEMPERATURE ( R. ENTHALPY , R. PRESSURE )                              
C                                                                               
C                                                                               
C        DIMENSION ::::                                                         
C                                                                               
C            TEMPERATURE  ... KELBIN                                            
C            ENRHALPY     ... KILO-JOULE PER KILO-GRAM                          
C            PRESSURE     ... PASCAL                                            
C                                                                               
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      REAL*8 FUNCTION RPRSHT(DEPSI,DTH)                                         
      IMPLICIT REAL*8 (D)                                                       
      COMPLEX*16 DSPH,RCHTP,CDHM,RSATHT,RH                                      
      REAL*8  BETAK,XPRSHT,EPSI2,RHTP                                           
C                                                                               
      COMMON / CTMPHT / DEPSIC,DTHC                                             
C                                                                               
      DATA DB1HPA,DBETA2 / 4.520795660 D-06 , 4.219990731 D+00 /                
C                                                                               
C                                                                               
C                                                                               
       EXTERNAL XPRSHT                                                          
C                                                                               
C                                                                               
C              
      write(6,*)'Warning : function RPRSHT'
c                                                                 
      DTHC=DTH                                                                  
      DEPSIC=DEPSI                                                              
C                                                                               
C                                                                               
C                                                                               
      DH1=EPSI2(DTH,DB1HPA)                                                     
      DSPH=RSATHT(DTH)                                                          
      DH2=DREAL(DSPH)                                                           
      DH3=DIMAG(DSPH)                                                           
      DH4=RHTP(DTH,DBETA2)                                                      
      DBETAS=BETAK(DTH)                                                         
C                                                                               
C                                                                               
C                                                                               
      IF( DEPSI .LE. DH2  .AND.  DEPSI .GT. DH3 ) THEN                          
          DBETA=DBETAS                                                          
          RPRSHT=DTH                                                            
      ELSE                                                                      
          IF ( DEPSI .GE. DH1 ) THEN                                            
               WRITE(6,6000)                                                    
               STOP                                                             
          ELSE                                                                  
               IF ( DEPSI .LT. DH4 )THEN                                        
                    WRITE(6,6100)                                               
                    STOP                                                        
               ELSE                                                             
                    IF ( DEPSI .LE. DH1 .AND. DEPSI .GT. DH2 ) THEN             
                         DPL=DB1HPA                                             
                         DPU=DBETAS                                             
                    ELSE                                                        
                         DPL=DBETAS                                             
                         DPU=DBETA2                                             
                    END IF                                                      
               END IF                                                           
          END IF                                                                
      END IF                                                                    
C                                                                               
C                                                                               
C                                                                               
      DO 210 I=1,20                                                             
C                                                                               
      DPM=DEXP((DLOG(DPL)+DLOG(DPU))/2.0D+00)                                   
      CALL RRGION(NR,DTH,DPM)                                                   

c      CDHM=RCHTP(DTH,DPM)                                                       
      CDHM=RHTP(DTH,DPM)                                                       
C                                                                               
      IF( NR.EQ.1 .OR. NR.EQ.4 ) DHM=DREAL(CDHM)                                
      IF( NR.EQ.2 .OR. NR.EQ.3 ) DHM=DIMAG(CDHM)                                
C                                                                               
C                                                                               
C                                                                               
      IF( DEPSI .GT. DHM) DPU=DPM                                               
      IF( DEPSI .LE. DHM) DPL=DPM                                               
C                                                                               
  210 CONTINUE                                                                  
      DBETA=(DPL+DPU)/2.0D+00                                                   
C                                                                               
C                                                                               
C                                                                               
C     CALL DNLBIT(DBETA,XTMPHT,DPL,DPU,1.0D-08,100,IER)                         
C     IF(IER.EQ.0) GO TO 510                                                    
C     WRITE(6,6200)                                                             
C6200 FORMAT(///,10X,'FUNCTION-TMPHP : IER ERROR CONDITION',//)                 
C                                                                               
C                                                                               
C                                                                               
  510 CONTINUE                                                                  
      RPRSHT=DBETA                                                              
      RETURN                                                                    
C                                                                               
C                                                                               
 6000 FORMAT(///,10X,'PRSSURE IS LESS 1 H. PASCAL (RPRSHT)',/)                  
 6100 FORMAT(////,10X,'PRSSURE CAN NOT BE SEARCHED (RPRSHT)',/)                 
C                                                                               
C                                                                               
      END                                                                       
