      G(I)=0.0D+00                                                              
      DO 60 J=1,L                                                               
      G(I)=G(I)+SB(I,J)*DX(IX(I,J)+1)                                           
   60 CONTINUE                                                                  
   50 CONTINUE                                                                  
C                                                                               
C                                                                               
