      DO 100 I=1,5                                                              
      D(2)=+D(2)-DFLOAT(I)*DFLOAT(I-1)*DB(I)/DB(2)*F(I)                         
  100 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(3)=0.0D+00                                                              
      DO 200 I=6,8                                                              
      DBM=(1.0D+00/DB(I-1)+G(I))**3                                             
      DAM=DFLOAT(I-2)*F(I)                                                      
     *    *(DFLOAT(I-3)/DB(2*I-1) - DFLOAT(I-1)/DB(I+1)*G(I))                   
      D(3)=D(3)-DAM/DBM                                                         
  200 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      D(4)=0.0D+00                                                              
      DO 300 I=1,7                                                              
      D(4)=D(4)+B9(I)*DX(I)                                                     
  300 CONTINUE                                                                  
