      D(4)=110.0D+00/DBL*(DBETA/DBL)**9*D(4)                                    
C                                                                               
C                                                                               
C                                                                               
      GAMA20=DSUM(D,4)                                                          
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
