     *   -6.508211677D+03 ,  5.745984054D+03 , -2.693088365D+03 ,               
     *    5.235718623D+02 /                                                     
C                                                                               
C     SMALL B PARAMETERS                                                        
C                                                                               
      DATA SB0,SB(6,1),SB(7,1),SB(8,1),SB(8,2)                                  
     * /  7.633333333D-01 ,  4.006073948D-01 ,  8.636081627D-02 ,               
     *   -8.532322921D-01 ,  3.460208861D-01 /                                  
C                                                                               
