      G(I) =G(I)  +                 SB(I,J)*DX(IX(I,J)+1)                       
      DG(I)=DG(I) + DFLOAT(IX(I,J))*SB(I,J)*DX(IX(I,J)+1)                       
  210 CONTINUE                                                                  
