     * /  6.670375918D-02 ,  1.388983801D+00 /                                  
      DATA B(2,1),B(2,2),B(2,3)                                                 
     * /  8.390104328D-02 ,  2.614670893D-02 , -3.373439453D-02 /               
      DATA B(3,1),B(3,2)                                                        
     * /  4.520918904D-01 ,  1.069036614D-01 /                                  
      DATA B(4,1),B(4,2)                                                        
     * / -5.975336707D-01 , -8.847535804D-02 /                                  
      DATA B(5,1),B(5,2),B(5,3)                                                 
     * /  5.958051609D-01 , -5.159303373D-01 ,  2.075021122D-01 /               
      DATA B(6,1),B(6,2)                                                        
