      DATA B(7,1),B(7,2)                                                        
     * /  1.683998803D-01 , -5.809438001D-02 /                                  
      DATA B(8,1),B(8,2)                                                        
     * /  6.552390126D-03 ,  5.710218649D-04 /                                  
      DATA (B9(I),I=1,7)                                                        
     * /  1.936587558D+02 , -1.388522425D+03 ,  4.126607219D+03 ,               
     *   -6.508211677D+03 ,  5.745984054D+03 , -2.693088365D+03 ,               
     *    5.235718623D+02 /                                                     
C                                                                               
C     SMALL B PARAMETERS                                                        
C                                                                               
      DATA SB0,SB(6,1),SB(7,1),SB(8,1),SB(8,2)                                  
     * /  7.633333333D-01 ,  4.006073948D-01 ,  8.636081627D-02 ,               
     *   -8.532322921D-01 ,  3.460208861D-01 /                                  
C                                                                               
C                                                                               
C                                                                               
      DT=DEXP(SB0*(1.0D+00-DTH))                                                
      DTEMP=DT                                                                  
