      D(2)=C3(1)*DX+C3(10)*DLOG(DX)                                             
      DO 250 I=2,9                                                              
      D(2)=D(2)+C3(I)/DXN(I)                                                    
  250 CONTINUE                                                                  
      D(2)=D(2)*6.0D+00*DTH1(2)                                                 
C                                                                               
C                                                                               
