C                                                                               
C                                                                               
C            ** SPECIFIC HEAT CAPACITY**                                        
C                                                                               
C               . REAL PART        : LIQUID PHASE                               
C               . IMAGINARY PART   : VAPOR  PHASE                               
C                                                                               
C                                                                               
C            ** DIMENSIONS **                                                   
C                                                                               
C              . SPECIFIC CAPACITY : KILO-JOULE / (KILO-GRAM * KELBIN )         
C              . TEMPERATURE       : KELBIN                                     
C              . PRESSURE          : PASCAL                                     
C                                                                               
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      SUBROUTINE HEATCP(T,P,SPC,NR)                                             
      IMPLICIT REAL*8 (D)                                                       
      COMPLEX CP,SPC                                                            
      REAL*8 PHAY1,PHAY2,PHAY3,PHAY4,VKAY3,VKAY4,BETAK                          
      REAL*8 VKAY1,VKAY2                                                        
C                                                                               
      DATA VC1,TC1,PC1                                                          
     * / 3.17 E-03 , 6.473D+02 , 2.212E+07 /                                    
C                                                                               
      DATA     DTC1 ,      DPC1                                                 
     * /  6.473D+02 , 2.212D+07 /                                               
C                                                                               
C                                                                               
C                                                                               
      DTH=DBLE(T)/DTC1                                                          
      DBETA=DBLE(P)/DPC1                                                        
C                                                                               
C                                                                               
C                                                                               
      CALL RRGION(NR,DTH,DBETA)                                                 
C                                                                               
C                                                                               
C                                                                               
      GO TO (10,20,30,40,50,60),NR                                              
C                                                                               
C                                                                               
C                                                                               
   10 CONTINUE                                                                  
      DCP=PHAY1(DTH,DBETA)                                                      
      CPC=SNGL(DCP)                                                             
      CP=CMPLX(CPC,0.0)                                                         
      GO TO 500                                                                 
C                                                                               
   20 CONTINUE                                                                  
      DCP=PHAY2(DTH,DBETA)                                                      
      CPC=SNGL(DCP)                                                             
      CP=CMPLX(0.0,CPC)                                                         
      GO TO 500                                                                 
C                                                                               
   30 CONTINUE                                                                  
      DX3=VKAY3(DTH,DBETA)                                                      
      DCP=PHAY3(DTH,DX3)                                                        
      CPC=SNGL(DCP)                                                             
      CP=CMPLX(0.0,CPC)                                                         
      GO TO 500                                                                 
C                                                                               
   40 CONTINUE                                                                  
      DX4=VKAY4(DTH,DBETA)                                                      
      DCP=PHAY4(DTH,DX4)                                                        
      CPC=SNGL(DCP)                                                             
      CP=CMPLX(CPC,0.0)                                                         
      GO TO 500                                                                 
C                                                                               
   50 CONTINUE                                                                  
      DBK=BETAK(DTH)                                                            
      DX3=VKAY3(DTH,DBK)                                                        
      DX4=VKAY4(DTH,DBK)                                                        
      DCP3=PHAY3(DTH,DX3)                                                       
      DCP4=PHAY4(DTH,DX4)                                                       
      CPC3=SNGL(DCP3)                                                           
      CPC4=SNGL(DCP4)                                                           
      CP=CMPLX(CPC4,CPC3)                                                       
      GO TO 500                                                                 
C                                                                               
   60 CONTINUE                                                                  
      DBK=BETAK(DTH)                                                            
      DCP1=PHAY1(DTH,DBK)                                                       
      DCP2=PHAY2(DTH,DBK)                                                       
      CPC1=SNGL(DCP1)                                                           
      CPC2=SNGL(DCP2)                                                           
      CP=CMPLX(CPC1,CPC2)                                                       
C                                                                               
C                                                                               
C                                                                               
  500 CONTINUE                                                                  
      SPC=VC1*PC1/TC1*CP                                                        
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
