C======================================================================         
C                                                                               
C      SUBROUTINE DCTP                                                          
C                                                                               
C        I=0(T=CONST)   I=1(P=CONST)                                            
C                                                                               
C======================================================================         
C                                                                               
      SUBROUTINE DCTP(FUNC,T,P,I,DTP,DF,EPS)                                    
C                                                                               
C                                                                               
C                                                                               
      IF (I.EQ.0) THEN                                                          
          DT=0.0                                                                
          DP=P/100.0                                                            
      ELSE                                                                      
          DT=T/100.0                                                            
          DP=0.0                                                                
      END IF                                                                    
C                                                                               
C                                                                               
C                                                                               
      J=0                                                                       
C                                                                               
C                                                                               
C                                                                               
   10 CONTINUE                                                                  
C                                                                               
C                                                                               
C                                                                               
      DFP=FUNC(T+DT/2.0,P+DP/2.0)                                               
      DFN=FUNC(T-DT/2.0,P-DP/2.0)                                               
      DF=(DFP-DFN)/(DT+DP)                                                      
C                                                                               
C                                                                               
C                                                                               
      IF ( ABS(1.0-DFN/DFP) .GE. 1.0E-07) GO TO 30                              
C                                                                               
C                                                                               
C                                                                               
      DF=0.0                                                                    
      GO TO 20                                                                  
C                                                                               
C                                                                               
C                                                                               
   30 CONTINUE                                                                  
      IF (J.EQ.0) THEN                                                          
          DFOLD=DF                                                              
      ELSE                                                                      
          DFNEW=DF                                                              
          IF ( ABS(1.0-DFOLD/DFNEW) .LT. EPS ) GO TO 20                         
          DFOLD=DFNEW                                                           
      END IF                                                                    
C                                                                               
C                                                                               
C                                                                               
      DT=DT/2.0                                                                 
      DP=DP/2.0                                                                 
      J=J+1                                                                     
      IF (J .GT. 20)   STOP                                                     
      GO TO 10                                                                  
C                                                                               
C                                                                               
C                                                                               
   20 CONTINUE                                                                  
      DTP=DT+DP                                                                 
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
