C              HEAT CAPACITY   :  JOULE / ( KILO-GRAM KELBIN )                  
C              PRESSURE        :  PACAL                                         
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      COMPLEX FUNCTION SDUTVP(P)                                              
      IMPLICIT REAL*8 (D)                                                       
      COMPLEX*16 XDUTVP,DCV                                                     
C                                                                               
C                                                                               
C                                                                               
      DATA       VC1 ,        TC1 ,        PC1                                  
     *   / 3.17 E-03 , 6.473 E+02 , 2.212 E+07  /                               
C                                                                               
C                                                                               
C                                                                               
      DATA DPC1  / 2.212 D+07 /                                                 
C                                                                               
C                                                                               
C                                                                               
      DBETA=DBLE(P)/DPC1                                                        
C                                                                               
C                                                                               
C                                                                               
      DCV=XDUTVP(DBETA)                                                         
C                                                                               
C                                                                               
C                                                                               
      SDUTVP=VC1*PC1/TC1*(DCV)                                             
