      COMPLEX FUNCTION SRCPVT(T)                                              
      COMPLEX*8 SDHTPT,SDUTVT                                                   
      COMPLEX*8  CDHTPT,CDUTVT                                                  
      REAL*4     GDHTPT,GDUTVT,IDHTPT,IDUTVT,GADEXT,IADEXT                      
C                                                                               
C                                                                               
C                                                                               
      CDHTPT=SDHTPT(T)                                                          
      CDUTVT=SDUTVT(T)                                                          
C                                                                               
C                                                                               
C                                                                               
      GDHTPT=REAL(CDHTPT)                                                       
      GDUTVT=REAL(CDUTVT)                                                       
C                                                                               
C                                                                               
C                                                                               
      IDHTPT=AIMAG(CDHTPT)                                                      
      IDUTVT=AIMAG(CDUTVT)                                                      
C                                                                               
C                                                                               
C                                                                               
      GADEXT=GDHTPT/GDUTVT                                                      
      IADEXT=IDHTPT/IDUTVT                                                      
C                                                                               
C                                                                               
C                                                                               
      SRCPVT=CMPLX(GADEXT,IADEXT)                                               
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
