C            ISENTROPIC EXPONENT  :  NON-DIMEMSION                              
C            TEMPERATURE          :  NON-DIMENSION                              
C                                                                               
C                                                                               
C+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++        
C                                                                               
      COMPLEX*16 FUNCTION XADEXT(DTH)                                           
      IMPLICIT REAL*8 (D)                                                       
      IMPLICIT COMPLEX*16 (C)                                                   
      COMPLEX*16 XDHTPT,XDVTPT,XDVPTT,RSATVT                                    
      REAL*8 GDVTP,GDVPT,GDHTP,GADEX,GV,                                        
     *       IDVTP,IDVPT,IDHTP,IADEX,IV                                         
      REAL*8 BETAK                                                              
C                                                                               
C                                                                               
C                                                                               
      DBETA=BETAK(DTH)                                                          
      CV=RSATVT(DTH)                                                            
      CDVTP=XDVTPT(DTH)                                                         
      CDVPT=XDVPTT(DTH)                                                         
      CDHTP=XDHTPT(DTH)                                                         
C                                                                               
C                                                                               
C                                                                               
      GV=DREAL(CV)                                                              
      GDVTP=DREAL(CDVTP)                                                        
      GDVPT=DREAL(CDVPT)                                                        
      GDHTP=DREAL(CDHTP)                                                        
C                                                                               
C                                                                               
C                                                                               
      IV=DIMAG(CV)                                                              
      IDVTP=DIMAG(CDVTP)                                                        
      IDVPT=DIMAG(CDVPT)                                                        
      IDHTP=DIMAG(CDHTP)                                                        
C                                                                               
C                                                                               
C                                                                               
      GADEX=-GV*GDHTP/DBETA/(GDHTP*GDVPT+DTH*GDVTP**2)                          
      IADEX=-IV*IDHTP/DBETA/(IDHTP*IDVPT+DTH*IDVTP**2)                          
C                                                                               
C                                                                               
C                                                                               
      XADEXT=DCMPLX(GADEX,IADEX)                                                
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
