      CDVPT=XDVPTT(DTH)                                                         
      CDHTP=XDHTPT(DTH)                                                         
C                                                                               
C                                                                               
C                                                                               
      GV=DREAL(CV)                                                              
      GDVTP=DREAL(CDVTP)                                                        
      GDVPT=DREAL(CDVPT)                                                        
      GDHTP=DREAL(CDHTP)                                                        
C                                                                               
C                                                                               
C                                                                               
      IV=DIMAG(CV)                                                              
      IDVTP=DIMAG(CDVTP)                                                        
      IDVPT=DIMAG(CDVPT)                                                        
      IDHTP=DIMAG(CDHTP)                                                        
C                                                                               
C                                                                               
C                                                                               
      GSNVT=DSQRT(-GV**2*GDHTP/(GDHTP*GDVPT+DTH*GDVTP**2))                      
      ISNVT=DSQRT(-IV**2*IDHTP/(IDHTP*IDVPT+DTH*IDVTP**2))                      
C                                                                               
C                                                                               
C                                                                               
      XSNVT=DCMPLX(GSNVT,ISNVT)                                                 
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
C                                                                               
C        REDUCED SONIC VELOCITY IN SATURATED CONDITION                          
C                                                                               
C                                                                               
C       ***   INPUT  :  PRESSURE                                                
C                                                                               
C                                                                               
C       ***   DIMENSION                                                         
C                                                                               
C                                                                               
C             SONIC VELOCITY   :  NON-DIMENSION                                 
C             PRESSURE         :  NON-DIMENSION                                 
C                                                                               
C                                                                               
C+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++        
C                                                                               
      COMPLEX*16 FUNCTION XSNVP(DBETA)                                          
      IMPLICIT REAL*8 (D)                                                       
      REAL*8  THETAK                                                            
      COMPLEX*16 XSNVT                                                          
C                                                                               
C                                                                               
C                                                                               
      DTH=THETAK(DBETA)                                                         
C                                                                               
C                                                                               
C                                                                               
      XSNVP=XSNVT(DTH)                                                          
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     R. TEMPERATURE ( R. ENTHALPY R. PRESSURE )                                
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION RTMPHP(DEPSI,DBETA)                                       
C                                                                               
      IMPLICIT REAL*8 (D)                                                       
      COMPLEX*16      DSPH,RSATHP                                               
      REAL*8          THETAK,THETAL,RHTP                                        
C                                                                               
      PARAMETER ( DEPS=1.0D-05, ITEND=100 )                                     
C                                                                               
      DATA      DTHT         ,  DTH1           ,   DTH3                         
     *     / 4.219990731D-01 , 9.626911787D-01 , 1.657886606D+00 /              
C                                                                               
      DATA      DBETAT       ,  DBETA1         ,  DBETA2                        
     *     / 2.763311032D-05 , 7.475191707D-01 , 4.520795660D+00/               
C                                                                               
      IF( DBETA .LE. 0.0 ) THEN                                                 
          WRITE(6,6000)                                                         
          RETURN                                                                
      ELSE IF( DBETA .GE. DBETA2 ) THEN                                         
          WRITE(6,6100)                                                         
          RETURN                                                                
      END IF                                                                    
C                                                                               
      DH0  = RHTP(DTHT,DBETA)                                                   
      IF( DEPSI .LT. DH0 ) THEN                                                 
          WRITE(6,6200)                                                         
          RETURN                                                                
      END IF                                                                    
C                                                                               
      DH3  = RHTP(DTH3,DBETA)                                                   
      IF( DEPSI .GT. DH3 ) THEN                                                 
          WRITE(6,6300)                                                         
          RETURN                                                                
      END IF                                                                    
C                                                                               
      IF( DBETA .LT. DBETAT ) THEN                                              
         DTL  = DTHT                                                            
         DHL  = DH0                                                             
         DTR  = DTH3                                                            
         DHR  = DH3                                                             
      ELSE IF( DBETA .LT. DBETA1 ) THEN                                         
         DTHS = THETAK(DBETA)                                                   
         DSPH = RSATHP(DBETA)                                                   
         IF( DEPSI .LT. DREAL(DSPH) ) THEN                                      
             DTL  = DTHT                                                        
             DHL  = DH0                                                         
             DTR  = DTHS                                                        
             DHR  = DREAL(DSPH)                                                 
         ELSE IF( DEPSI .GT. DIMAG(DSPH) ) THEN                                 
             DTL  = DTHS                                                        
             DHL  = DIMAG(DSPH)                                                 
             DTR  = DTH3                                                        
             DHR  = DH3                                                         
         ELSE                                                                   
             RTMPHP= DTHS                                                       
             RETURN                                                             
         END IF                                                                 
      ELSE IF( DBETA .LT. 1.0D+00 ) THEN                                        
         DTHS = THETAK(DBETA)                                                   
         DSPH = RSATHP(DBETA)                                                   
         IF( DEPSI .LT. DREAL(DSPH) ) THEN                                      
             DTHM = DTH1                                                        
             DHM  = RHTP(DTHM,DBETA)                                            
             IF( DEPSI .LT. DHM ) THEN                                          
                 DTL  = DTHT                                                    
                 DHL  = DH0                                                     
                 DTR  = DTHM                                                    
                 DHR  = DHM                                                     
             ELSE IF( DEPSI .GT. DHM ) THEN                                     
                 DTL  = DTHM                                                    
                 DHL  = DHM                                                     
                 DTR  = DTHS                                                    
                 DHR  = DREAL(DSPH)                                             
             ELSE                                                               
                 RTMPHP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE IF( DEPSI .GT. DIMAG(DSPH) ) THEN                                 
             DTHM = THETAL(DBETA)                                               
             DHM  = RHTP(DTHM,DBETA)                                            
             IF( DEPSI .LT. DHM ) THEN                                          
                 DTL  = DTHS                                                    
                 DHL  = DIMAG(DSPH)                                             
                 DTR  = DTHM                                                    
                 DHR  = DHM                                                     
             ELSE IF( DEPSI .GT. DHM ) THEN                                     
                 DTL  = DTHM                                                    
                 DHL  = DHM                                                     
                 DTR  = DTH3                                                    
                 DHR  = DH3                                                     
             ELSE                                                               
                 RTMPHP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE                                                                   
             RTMPHP= DTHS                                                       
             RETURN                                                             
         END IF                                                                 
      ELSE                                                                      
         DTHS = 1.0D+00                                                         
         DHS  = RHTP(DTHS,DBETA)                                                
         IF( DEPSI .LT. DHS ) THEN                                              
             DTHM = DTH1                                                        
             DHM  = RHTP(DTHM,DBETA)                                            
             IF( DEPSI .LT. DHM ) THEN                                          
                 DTL  = DTHT                                                    
                 DHL  = DH0                                                     
                 DTR  = DTHM                                                    
                 DHR  = DHM                                                     
             ELSE IF( DEPSI .GT. DHM ) THEN                                     
                 DTL  = DTHM                                                    
                 DHL  = DHM                                                     
                 DTR  = DTHS                                                    
                 DHR  = DHS                                                     
             ELSE                                                               
                 RTMPHP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE IF( DEPSI .GT. DHS ) THEN                                         
             DTHM = THETAL(DBETA)                                               
             DHM  = RHTP(DTHM,DBETA)                                            
             IF( DEPSI .LT. DHM ) THEN                                          
                 DTL  = DTHS                                                    
                 DHL  = DHS                                                     
                 DTR  = DTHM                                                    
                 DHR  = DHM                                                     
             ELSE IF( DEPSI .GT. DHM ) THEN                                     
                 DTL  = DTHM                                                    
                 DHL  = DHM                                                     
                 DTR  = DTH3                                                    
                 DHR  = DH3                                                     
             ELSE                                                               
                 RTMPHP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE                                                                   
             RTMPHP= DTHS                                                       
             RETURN                                                             
         END IF                                                                 
      END IF                                                                    
C                                                                               
      DO 10 I=1,ITEND                                                           
        DTM  = 0.5D+00*(DTL + DTR)                                              
        DHM  = RHTP(DTM,DBETA)                                                  
C                                                                               
             IF( DHM .GT. DEPSI ) THEN                                          
                 DTR  = DTM                                                     
                 DHR  = DHM                                                     
             ELSE IF( DHM .LT. DEPSI ) THEN                                     
                 DTL  = DTM                                                     
                 DHL  = DHM                                                     
             END IF                                                             
C                                                                               
        DTM  = ( DTL*(DHR - DEPSI) - DTR*(DHL - DEPSI) )/(DHR - DHL)            
        DHM  = RHTP(DTM,DBETA)                                                  
        DET  = (DHM - DEPSI)/DEPSI                                              
C                                                                               
             IF( ABS(DET) .LE. DEPS ) THEN                                      
                 GO TO 1000                                                     
             ELSE IF( DET .GT. 0.0D+00 ) THEN                                   
                 DTR  = DTM                                                     
                 DHR  = DHM                                                     
             ELSE IF( DET .LT. 0.0D+00 ) THEN                                   
                 DTL  = DTM                                                     
                 DHL  = DHM                                                     
             END IF                                                             
C                                                                               
 10   CONTINUE                                                                  
      WRITE(6,6400)                                                             
 1000 CONTINUE                                                                  
      RTMPHP= DTM                                                               
      RETURN                                                                    
C                                                                               
 6000 FORMAT(//,10X,'(FUNC.TMPHP) INPUTED PRESSURE IS .LT. 0.0(MPA)')           
 6100 FORMAT(//,10X,'(FUNC.TMPHP) INPUTED PRESSURE IS .GT. 100(MPA)')           
 6200 FORMAT(//,10X,'(FUNC.TMPHP) INPUTED ENTHALPY IS .LT. ENTHALPY'            
     *          ,' AT 0 (C)',/)                                                 
 6300 FORMAT(//,10X,'(FUNC.TMPHP) INPUTED ENTHALPY IS .GT. ENTHALPY'            
     *          ,' AT 800 (C)',/)                                               
 6400 FORMAT(//,10X,'(FUNC.TMPHP) ERROR RETURN AT HALF INTERVAL MTH.')          
      END                                                                       
C***********************************************************************        
C                                                                               
C     R. TEMPERATURE ( R. VOLUME , R. PRESSURE )                                
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION RTMPVP(DVKAY,DBETA)                                       
C                                                                               
      IMPLICIT REAL*8 (D)                                                       
      COMPLEX*16      DSPV,RSATVP                                               
      REAL*8          THETAK,THETAL,RVTP                                        
C                                                                               
      PARAMETER ( DEPS=1.0D-05, ITEND=100 )                                     
C                                                                               
      DATA      DTHT         ,  DTH1           ,   DTH3                         
     *     / 4.219990731D-01 , 9.626911787D-01 , 1.657886606D+00 /              
C                                                                               
      DATA      DBETAT       ,  DBETA1         ,  DBETA2                        
     *     / 2.763311032D-05 , 7.475191707D-01 , 4.520795660D+00/               
C                                                                               
      IF( DBETA .LE. 0.0 ) THEN                                                 
          WRITE(6,6000)                                                         
          RETURN                                                                
      ELSE IF( DBETA .GE. DBETA2 ) THEN                                         
          WRITE(6,6100)                                                         
          RETURN                                                                
      END IF                                                                    
C                                                                               
      DV0  = RVTP(DTHT,DBETA)                                                   
      IF( DVKAY .LT. DV0 ) THEN                                                 
          WRITE(6,6200)                                                         
          RETURN                                                                
      END IF                                                                    
C                                                                               
      DV3  = RVTP(DTH3,DBETA)                                                   
      IF( DVKAY .GT. DV3 ) THEN                                                 
          WRITE(6,6300)                                                         
          RETURN                                                                
      END IF                                                                    
C                                                                               
      IF( DBETA .LT. DBETAT ) THEN                                              
         DTL  = DTHT                                                            
         DVL  = DV0                                                             
         DTR  = DTH3                                                            
         DVR  = DV3                                                             
      ELSE IF( DBETA .LT. DBETA1 ) THEN                                         
         DTHS = THETAK(DBETA)                                                   
         DSPV = RSATVP(DBETA)                                                   
         IF( DVKAY .LT. DREAL(DSPV) ) THEN                                      
             DTL  = DTHT                                                        
             DVL  = DV0                                                         
             DTR  = DTHS                                                        
             DVR  = DREAL(DSPV)                                                 
         ELSE IF( DVKAY .GT. DIMAG(DSPV) ) THEN                                 
             DTL  = DTHS                                                        
             DVL  = DIMAG(DSPV)                                                 
             DTR  = DTH3                                                        
             DVR  = DV3                                                         
         ELSE                                                                   
             RTMPVP= DTHS                                                       
             RETURN                                                             
         END IF                                                                 
      ELSE IF( DBETA .LT. 1.0D+00 ) THEN                                        
         DTHS = THETAK(DBETA)                                                   
         DSPV = RSATVP(DBETA)                                                   
         IF( DVKAY .LT. DREAL(DSPV) ) THEN                                      
             DTHM = DTH1                                                        
             DVM  = RVTP(DTHM,DBETA)                                            
             IF( DVKAY .LT. DVM ) THEN                                          
                 DTL  = DTHT                                                    
                 DVL  = DV0                                                     
                 DTR  = DTHM                                                    
                 DVR  = DVM                                                     
             ELSE IF( DVKAY .GT. DVM ) THEN                                     
                 DTL  = DTHM                                                    
                 DVL  = DVM                                                     
                 DTR  = DTHS                                                    
                 DVR  = DREAL(DSPV)                                             
             ELSE                                                               
                 RTMPVP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE IF( DVKAY .GT. DIMAG(DSPV) ) THEN                                 
             DTHM = THETAL(DBETA)                                               
             DVM  = RVTP(DTHM,DBETA)                                            
             IF( DVKAY .LT. DVM ) THEN                                          
                 DTL  = DTHS                                                    
                 DVL  = DIMAG(DSPV)                                             
                 DTR  = DTHM                                                    
                 DVR  = DVM                                                     
             ELSE IF( DVKAY .GT. DVM ) THEN                                     
                 DTL  = DTHM                                                    
                 DVL  = DVM                                                     
                 DTR  = DTH3                                                    
                 DVR  = DV3                                                     
             ELSE                                                               
                 RTMPVP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE                                                                   
             RTMPVP= DTHS                                                       
             RETURN                                                             
         END IF                                                                 
      ELSE                                                                      
         DTHS = 1.0D+00                                                         
         DVS  = RVTP(DTHS,DBETA)                                                
         IF( DVKAY .LT. DVS ) THEN                                              
             DTHM = DTH1                                                        
             DVM  = RVTP(DTHM,DBETA)                                            
             IF( DVKAY .LT. DVM ) THEN                                          
                 DTL  = DTHT                                                    
                 DVL  = DV0                                                     
                 DTR  = DTHM                                                    
                 DVR  = DVM                                                     
             ELSE IF( DVKAY .GT. DVM ) THEN                                     
                 DTL  = DTHM                                                    
                 DVL  = DVM                                                     
                 DTR  = DTHS                                                    
                 DVR  = DVS                                                     
             ELSE                                                               
                 RTMPVP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE IF( DVKAY .GT. DVS ) THEN                                         
             DTHM = THETAL(DBETA)                                               
             DVM  = RVTP(DTHM,DBETA)                                            
             IF( DVKAY .LT. DVM ) THEN                                          
                 DTL  = DTHS                                                    
                 DVL  = DVS                                                     
                 DTR  = DTHM                                                    
                 DVR  = DVM                                                     
             ELSE IF( DVKAY .GT. DVM ) THEN                                     
                 DTL  = DTHM                                                    
                 DVL  = DVM                                                     
                 DTR  = DTH3                                                    
                 DVR  = DV3                                                     
             ELSE                                                               
                 RTMPVP= DTHM                                                   
                 RETURN                                                         
             END IF                                                             
         ELSE                                                                   
             RTMPVP= DTHS                                                       
             RETURN                                                             
         END IF                                                                 
      END IF                                                                    
C                                                                               
      DO 10 I=1,ITEND                                                           
        DTM  = 0.5D+00*(DTL + DTR)                                              
        DVM  = RVTP(DTM,DBETA)                                                  
C                                                                               
             IF( DVM .GT. DVKAY ) THEN                                          
                 DTR  = DTM                                                     
                 DVR  = DVM                                                     
             ELSE IF( DVM .LT. DVKAY ) THEN                                     
                 DTL  = DTM                                                     
                 DVL  = DVM                                                     
             END IF                                                             
C                                                                               
        DTM  = ( DTL*(DVR - DVKAY) - DTR*(DVL - DVKAY) )/(DVR - DVL)            
        DVM  = RVTP(DTM,DBETA)                                                  
        DET  = (DVM - DVKAY)/DVKAY                                              
C                                                                               
             IF( ABS(DET) .LE. DEPS ) THEN                                      
                 GO TO 1000                                                     
             ELSE IF( DET .GT. 0.0D+00 ) THEN                                   
                 DTR  = DTM                                                     
                 DVR  = DVM                                                     
             ELSE IF( DET .LT. 0.0D+00 ) THEN                                   
                 DTL  = DTM                                                     
                 DVL  = DVM                                                     
             END IF                                                             
C                                                                               
 10   CONTINUE                                                                  
      WRITE(6,6400)                                                             
 1000 CONTINUE                                                                  
      RTMPVP= DTM                                                               
      RETURN                                                                    
C                                                                               
 6000 FORMAT(//,10X,'(FUNC.TMPVP) INPUTED PRESSURE IS .LT. 0.0(MPA)')           
 6100 FORMAT(//,10X,'(FUNC.TMPVP) INPUTED PRESSURE IS .GT. 100(MPA)')           
 6200 FORMAT(//,10X,'(FUNC.TMPVP) INPUTED VOLUME   IS .LT. VOLUME'              
     *          ,' AT 0 (C)',/)                                                 
 6300 FORMAT(//,10X,'(FUNC.TMPVP) INPUTED VOLUME   IS .GT. VOLUME'              
     *          ,' AT 800 (C)',/)                                               
 6400 FORMAT(//,10X,'(FUNC.TMPVP) ERROR RETURN AT HALF INTERVAL MTH.')          
      END                                                                       
C***********************************************************************        
C                                                                               
C      TEMPERATURE ( SPECIFIC ENTHALPY , PRESSURE )                             
C                                                                               
C***********************************************************************        
      REAL FUNCTION TMPHP(H,P)                                                  
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 RTMPHP                                                             
C                                                                               
      DATA     TC1       ,     DVC1    ,     DPC1                               
     *     / 647.3       ,  3.17 D-03  ,  2.212 D+07 /                          
C                                                                               
      DBETA=DBLE(P)/DPC1                                                        
      DEPSI=DBLE(H)/(DPC1*DVC1)                                                 
C                                                                               
      DTEMP=RTMPHP(DEPSI,DBETA)                                                 
C                                                                               
      TMPHP=SNGL(DTEMP)*TC1                                                     
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      TEMPERATURE ( SPECIFIC VOLUME , PRESSURE )                               
C                                                                               
C***********************************************************************        
      REAL FUNCTION TMPVP(V,P)                                                  
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 RTMPVP                                                             
C                                                                               
      DATA     TC1       ,     DVC1    ,     DPC1                               
     *     / 647.3       ,  3.17 D-03  ,  2.212 D+07 /                          
C                                                                               
      DBETA=DBLE(P)/DPC1                                                        
      DVKAY=DBLE(V)/DVC1                                                        
C                                                                               
      DTEMP=RTMPVP(DVKAY,DBETA)                                                 
C                                                                               
      TMPVP=SNGL(DTEMP)*TC1                                                     
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     THE K-FUNCTION (SATURATION LINE)                                          
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION BETAK(DTH)                                                
      IMPLICIT REAL*8 (D)                                                       
      DIMENSION DK(9)                                                           
C                                                                               
      DATA (DK(I),I=1,9)                                                        
     * / -7.691234564D+00 , -2.608023696D+01 , -1.681706546D+02 ,               
     *    6.423285504D+01 , -1.189646225D+02 ,  4.167117320D+00 ,               
     *    2.097506760D+01 ,  1.0        D+09 ,  6.0        D+00 /               
C                                                                               
      DONTH=1.0D+00-DTH                                                         
      DTS  =0.0D+00                                                             
      DTP  =DONTH                                                               
      DO 10 I=1,5                                                               
           DTS=DTS+DTP*DK(I)                                                    
           DTP=DTP*DONTH                                                        
   10 CONTINUE                                                                  
C                                                                               
      DT1=DTH*(1.0D+00+DK(6)*DONTH+DK(7)*DONTH**2)                              
      DT2=DK(8)*DONTH**2+DK(9)                                                  
      DTP=DTS/DT1-DONTH/DT2                                                     
C                                                                               
      BETAK=DEXP(DTP)                                                           
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C            R.SATURATION PRESSURE ( R. SATURATION TEMPERATURE )                
C                                                                               
C        ** DIMENSION **                                                        
C                                                                               
C               R. PRESSURE    : NON-DIMENSION =P/PC                            
C               R. TEMPERATURE : NON-DIMENSION =T/TC                            
C                                                                               
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION THETAK(DBETA)                                             
C                                                                               
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 BETAK                                                              
C                                                                               
      PARAMETER (DEPS=1.0D-12, ITEND=200)                                       
C                                                                               
      IF(DBETA.GT.1.0D+00) THEN                                                 
            WRITE(6,6000) DBETA                                                 
            RETURN                                                              
      END IF                                                                    
C                                                                               
      DT0   = 1.0D+00                                                           
      DB0   = 1.0D+00                                                           
      DBDT0 = 7.857901231D+00                                                   
C                                                                               
      DO 10 I=1,ITEND                                                           
            DT1   = DT0 - (DB0-DBETA)/DBDT0                                     
            DS    = DABS((DT1-DT0)/DT1)                                         
            IF( DS .LE. DEPS ) THEN                                             
                  GO TO 1000                                                    
            ELSE                                                                
                  DT0   =  DT1                                                  
                  DB0   =  BETAK(DT1)                                           
                  DBDT0 = DBETAK(DT1)                                           
            END IF                                                              
 10   CONTINUE                                                                  
        GO TO 9999                                                              
 1000 CONTINUE                                                                  
      THETAK= DT1                                                               
      RETURN                                                                    
C                                                                               
 9999 CONTINUE                                                                  
      WRITE(6,6000)                                                             
      RETURN                                                                    
 6000 FORMAT(///,10X,'(FUNC.STEMP) P .GT. PC '                                  
     *       ,//,10X,'P/PC = ',1PD12.5                             )            
 6100 FORMAT(///,10X,'(FUNC.STEMP) NEWTON-METHOD IS NO-CONVERGENSE')            
      END                                                                       
C***********************************************************************        
C                                                                               
C     THE DIFFERENTIAL FUNCTION OF THE K-FUNCTION                               
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION  DBETAK(DTH)                                              
C                                                                               
      REAL*8    DK(9) , DTH , DTH1 , DX , G , DG , F , DF , H                   
     *        , ARG   , DARG                                                    
C                                                                               
      DATA (DK(I),I=1,9 )                                                       
     *   / -7.691234564D+00 , -2.608023696D+01 , -1.681706546D+02               
     *   ,  6.423285504D+01 , -1.189646225D+02 ,  4.167117320D+00               
     *   ,  2.097506760D+01 ,  1.000000000D+09 ,  6.000000000D+00  /            
C                                                                               
      DTH1  = 1.0D+00 - DTH                                                     
C                                                                               
      DX    = 1.0D+00                                                           
      G     = 0.0D+00                                                           
      DG    = 0.0D+00                                                           
          DO 10 I=1,5                                                           
               DG    =DG - DFLOAT(I)*DK(I)*DX                                   
               DX    =DX*DTH1                                                   
               G     =G  +           DK(I)*DX                                   
 10       CONTINUE                                                              
C                                                                               
      F    = DTH*( 1.0D+00 + DK(6)*DTH1 + DK(7)*DTH1**2 )                       
      DF   = 1.0D+00  + DK(6)*(1.0D+00-2.0D+00*DTH)                             
     *                + DK(7)*(1.0D+00-3.0D+00*DTH)*DTH1                        
      H    = DK(8)*DTH1**2 + DK(9)                                              
C                                                                               
      ARG  = G/F  -  DTH1/H                                                     
      DARG = G/F*(DG/G - DF/F)  + 1.0D+00/H  - 2.0D+00*DK(8)*(DTH1/H)**2        
C                                                                               
      DBETAK= DARG*DEXP(ARG)                                                    
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     THE L-FUNCTION                                                            
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION BETAL(DTH)                                                
      IMPLICIT REAL*8 (D)                                                       
      DIMENSION DL(3)                                                           
C                                                                               
      DATA (DL(I),I=1,3)                                                        
     * / 1.574373327D+01 , -3.417061978D+01 ,  1.931380707D+01 /                
C                                                                               
      BETAL=DL(1)+DTH*(DL(2)+DL(3)*DTH)                                         
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     FUNCTION THETAL                                                           
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION THETAL(DBETA)                                             
C                                                                               
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 L(3)                                                               
C                                                                               
      DATA (L(I),I=1,3)                                                         
     * / 1.574373327D+01 , -3.417061978D+01 ,  1.931380707D+01 /                
C                                                                               
      THETAL=(-L(2)+DSQRT(L(2)**2-4.0D+00*L(2)*(L(1)-DBETA)))                   
     *       /(2.0D+00*L(3))                                                    
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     THE DIFFERENTIAL FUNCTION OF THE L-FUNCTION                               
C                                                                               
C***********************************************************************        
      REAL*8 FUNCTION DBETAL(DTH)                                               
      IMPLICIT REAL*8 (D)                                                       
      DIMENSION DL(3)                                                           
C                                                                               
      DATA (DL(I),I=1,3)                                                        
     * / 1.574373327D+01 , -3.417061978D+01 ,  1.931380707D+01 /                
C                                                                               
      DBETAL=DL(2)+2.0D+00*DL(3)*DTH                                            
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C            SATURATION PRESSURE (  SATURATION TEMPERATURE )                    
C                                                                               
C        ** DIMENSION **                                                        
C                                                                               
C                PRESSURE    : PASCAL                                           
C                TEMPERATURE : KELBIN                                           
C                                                                               
C                                                                               
C***********************************************************************        
      REAL FUNCTION SPRET(T)                                                    
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 BETAK                                                              
C                                                                               
      DATA   PC1      ,  DTC1                                                   
     *    / 2.212E+07 , 6.473D+02 /                                             
C                                                                               
      DTH=DBLE(T)/DTC1                                                          
C                                                                               
      DPRES=BETAK(DTH)                                                          
C                                                                               
      SPRET=SNGL(DPRES)*PC1                                                     
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C            SATURATION TEMPERATURE ( SATURATION PRESSURE )                     
C                                                                               
C        ** DIMENSION **                                                        
C                                                                               
C                PRESSURE    : PASCAL                                           
C                TEMPERATURE : KELBIN                                           
C                                                                               
C                                                                               
C***********************************************************************        
      REAL FUNCTION STEMP(P)                                                    
      IMPLICIT REAL*8 (D)                                                       
      REAL*8 THETAK                                                             
C                                                                               
      DATA   DPC1     ,  TC1                                                    
     *    / 2.212D+07 , 6.473E+02 /                                             
C                                                                               
      DBETA=DBLE(P)/DPC1                                                        
C                                                                               
      DTH  =THETAK(DBETA)                                                       
C                                                                               
      STEMP=SNGL(DTH)*TC1                                                       
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     DIFFERENTIAL FUNCTION OF SPRET(TS)                                        
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION  DPSDTS(TS)                                               
      REAL*8 DPC1,DTC1,DTH,DPT,DBETAK                                           
C                                                                               
      DATA     DPC1      ,  DTC1                                                
     *      / 2.212D+07  , 6.473D+02 /                                          
C                                                                               
      DTH  = DBLE(TS)/DTC1                                                      
C                                                                               
      DPT  = DBETAK(DTH)*DPC1/DTC1                                              
C                                                                               
      DPSDTS= SNGL(DPT)                                                         
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     DIFFERENTIAL FUNCTION OF STEMP(PS)                                        
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION DTSDPS(PS)                                                
C                                                                               
      REAL*8 DPC1,DTC1,DBETA,DTH,THETAK,DBETAK,DTP                              
C                                                                               
      DATA     DPC1      ,  DTC1                                                
     *      / 2.212D+07  , 6.473D+02 /                                          
C                                                                               
      DBETA= DBLE(PS)/DPC1                                                      
      DTH  = THETAK(DBETA)                                                      
C                                                                               
      DTP  = DTC1/(DPC1*DBETAK(DTH))                                            
C                                                                               
      DTSDPS=SNGL(DTP)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     DIFFERENTIAL FUNCTION OF SATVP(PS)                                        
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SDVP(PS)                                               
C                                                                               
      COMPLEX*16    CDVP,XDVPTP,CDVT,XDVTPT                                     
C                                                                               
      REAL*8        DBETA,DTH,THETAK,DPT,DBETAK,DPC1,DVC1                       
C                                                                               
      DATA     DPC1      ,  DVC1                                                
     *      / 2.212D+07  , 3.170D-03 /                                          
C                                                                               
      DBETA= DBLE(PS)/DPC1                                                      
C                                                                               
C                                                                               
      CDVP = XDVPTP(DBETA)                                                      
C                                                                               
      DTH  = THETAK(DBETA)                                                      
C                                                                               
      CDVT = XDVTPT( DTH )                                                      
C                                                                               
      DPT  = DBETAK( DTH )                                                      
C                                                                               
      SDVP = ( DVC1/DPC1*(CDVP + CDVT/DPT) )                               
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C     DIFFERENTIAL FUNCTION OF SATHP(PS)                                        
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SDHP(PS)                                               
C                                                                               
      COMPLEX*16    CDHP,XDHPTP,CDHT,XDHTPT                                     
C                                                                               
      REAL*8        DBETA,DTH,THETAK,DPT,DBETAK,DPC1,DVC1                       
C                                                                               
      DATA     DPC1      ,  DVC1                                                
     *      / 2.212D+07  , 3.170D-03 /                                          
C                                                                               
      DBETA= DBLE(PS)/DPC1                                                      
C                                                                               
C                                                                               
      CDHP = XDHPTP(DBETA)                                                      
C                                                                               
      DTH  = THETAK(DBETA)                                                      
C                                                                               
      CDHT = XDHTPT( DTH )                                                      
C                                                                               
      DPT  = DBETAK( DTH )    
C                                                                               
      SDHP = ( DVC1*(CDHP + CDHT/DPT) )   
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           SURFACE TENSION OF WATER (TEMPERATURE)                              
C        ** DIMENSIONS **                                                       
C           SURFACE TENSION : NEWTON/METER                                      
C           TEMPERATURE : KELBIN                                                
C***********************************************************************        
      REAL*4 FUNCTION SIGMA(T)                                                  
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T                                                                  
C                                                                               
      DATA A,B,TC/2.358D-01,6.25D-01,6.4715D+02/                                
C     TC: CRITICAL TEMPERATURE                                                  
      TD=DBLE(T)                                                                
      IF(TD.GE.TC) THEN                                                         
      SIGMAD=0.0D+00                                                            
      WRITE(6,6000) T                                                           
 6000 FORMAT(1H1,5X,'(FUNC.SIGMA) T IS GREATER THAN 647.15 KELBIN'/             
     *       1H ,10X,'T=',1PE12.5)                                              
      GO TO 1000                                                                
      END IF                                                                    
      SIGMAD=A*(1.0D+00-TD/TC)**1.256*(1.0D+00-B*(1.0D+00-TD/TC))               
      SIGMA=SNGL(SIGMAD)                                                        
C                                                                               
 1000 CONTINUE                                                                  
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           THERMAL CONDUCTIVITY OF WATER (TEMPERATURE,PRESSURE)                
C        ** DIMENSION **                                                        
C           THERMAL CONDUCTIVITY :  WATT/METER*KELBIN                           
C           TEMPERATURE : KELDIN                                                
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      REAL*4 FUNCTION  RMTP(T,P)                                                
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,P,VTP                                                            
C                                                                               
      DATA T0,V0/6.473D+02,3.14762D-03/                                         
      DATA A0,A1,A2,A3/1.02811D-02,2.99621D-02,1.56146D-02,-4.22464D-03/        
      DATA B0,B1,B2/-3.97070D-01,4.00302D-01,1.06000D+00/                       
      DATA D1,D2,D3,D4/7.01309D-02,1.18520D-02,1.69937D-03,-1.02000D+00/        
      DATA BB1,BB2/-1.71587D-01,2.39219D+00/                                    
      DATA CC1,CC2,CC3,CC4,CC5,CC6                                              
     *    /6.42857D-01,-4.11717D+00,-6.17937D+00,                               
     *     3.08976D-03,8.22994D-02,1.00932D+01/                                 
C                                                                               
      VTPD=DBLE(VTP(T,P))                                                       
      TD=DBLE(T)                                                                
      VN=VTPD/V0                                                                
      TN=TD/T0                                                                  
C                                                                               
      RM0=TN**0.5*(A0+A1*TN+A2*TN**2+A3*TN**3)                                  
C                                                                               
      RMB=B0+B1/VN+B2*DEXP(BB1*(1.0D+00/VN+BB2)**2.0)                           
C                                                                               
      DT0=DABS(TN-1.0D+00)+CC4                                                  
      Q=2.0D+00+CC5/DT0**0.6                                                    
      R=Q+1.0D+00                                                               
      IF(TN.GE.1.0D+00) THEN                                                    
      S=1.0D+00/DT0                                                             
      ELSE                                                                      
      S=CC6/DT0**0.6                                                            
      END IF                                                                    
      X=CC1*(1.0D+00-1.0D+00/VN**2.8)                                           
      Y=Q/R*(1.0D+00-1.0D+00/VN**R)                                             
      Z=CC2*TN**1.5+CC3*VN**5.0                                                 
      DRM=DEXP(X)*(D1/TN**10.0+D2)/VN**1.8                                      
     *   +DEXP(Y)*D3*S/VN**Q                                                    
     *   +DEXP(Z)*D4                                                            
C                                                                               
      RMTPD=RM0+RMB+DRM                                                         
      RMTP=SNGL(RMTPD)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           DYNAMIC VISCOSITY OF WATER (TEMPERATURE,PRESSURE)                   
C        ** DIMENSION **                                                        
C           DYNAMIC VISCOSITY : PASCAL*SEC                                      
C           TEMPERATURE : KELBIN                                                
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      REAL*4 FUNCTION  DVTP(T,P)                                                
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,P,VTP                                                            
      DIMENSION A(4),B(6,5)                                                     
      DATA T0,V0/6.4727D+02,3.14762D-03/                                        
      DATA (A(I),I=1,4)                                                         
     *    /1.81583D-02,1.77624D-02,1.05287D-02,-3.6744D-03/                     
      DATA ((B(I,J),I=1,6),J=1,5)                                               
     *   /5.01938D-01,1.62888D-01,-1.30356D-01,                                 
     *              9.07919D-01,-5.51119D-01,1.46543D-01,                       
     *    2.35622D-01,7.89393D-01,6.73665D-01,                                  
     *              1.207552D+00,6.70665D-02,-8.43370D-02,                      
     *   -2.74637D-01,-7.43539D-01,-9.59456D-01,                                
     *             -6.87343D-01,-4.97089D-01,1.95286D-01,                       
     *    1.45831D-01,2.63129D-01,3.47247D-01,                                  
     *              2.13486D-01,1.00754D-01,-3.2932D-02,                        
     *   -2.70448D-02,-2.53093D-02,-2.67758D-02,                                
     *             -8.22904D-02,6.02253D-02,-2.02595D-02/                       
C                                                                               
      TD=DBLE(T)                                                                
      VTPD=DBLE(VTP(T,P))                                                       
      PTPD=1.0D+00/VTPD                                                         
      IF(PTPD.LE.0.0D+00.OR.PTPD.GE.1.05D+03) THEN                              
      WRITE(6,6000) PTPD                                                        
 6000 FORMAT(5X,'PTP IS MORE THAN 1050 OR LESS THAN 0'/                         
     *      15X,'PTP=',1E12.5)                                                  
      GO TO 30                                                                  
      END IF                                                                    
      TN=TD/T0                                                                  
      VN=VTPD/V0                                                                
C                                                                               
      P1=0.0D+00                                                                
      DO 10 I=1,4                                                               
      P1=P1+A(I)/TN**(I-1)                                                      
   10 CONTINUE                                                                  
      DV0=TN**0.5/P1                                                            
C                                                                               
      P2=0.0D+00                                                                
      DO 20 I=1,6                                                               
      DO 20 J=1,5                                                               
      P2=P2+B(I,J)*(1.0D+00/TN-1.0D+00)**(I-1)                                  
     *    *(1.0D+00/VN-1.0D+00)**(J-1)                                          
   20 CONTINUE                                                                  
C                                                                               
      DVTPD=DV0*DEXP(P2/VN)*1.0D-06                                             
      DVTP=SNGL(DVTPD)                                                          
C                                                                               
   30 CONTINUE                                                                  
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           KINETIC VISCOSITY OF WATER (TEMPERATURE,PRESSURE)                   
C        ** DIMENSION **                                                        
C           KINETIC VISCOSITY : PASCAL*SEC                                      
C           TEMPERATURE : KELBIN                                                
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      REAL*4 FUNCTION  KVTP(T,P)                                                
      IMPLICIT REAL*8 (D,V)                                                     
      REAL*4 T,P,VTP,DVTP                                                       
C                                                                               
      VTPD=DBLE(VTP(T,P))                                                       
      DVTPD=DBLE(DVTP(T,P))                                                     
C                                                                               
      DKVTP=VTPD*DVTPD                                                          
      KVTP=SNGL(DKVTP)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           THERMAL CONDUCTIVITY OF SATURATED WATER (TEMPERATURE)               
C             REAL PART : LIQUID FASE                                           
C             IMAGINALY PART : GASS FASE                                        
C        ** DIMENSION **                                                        
C           THERMAL CONDUCTIVITY :  WATT/METER*KELBIN                           
C           TEMPERATURE : KELDIN                                                
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      COMPLEX FUNCTION  SATRMT(T)                                             
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,SVTL,SVTG,SRMTL,SRMTG                                            
      COMPLEX*8 SATVT                                                           
C                                                                               
      DATA T0,V0/6.473D+02,3.14762D-03/                                         
      DATA A0,A1,A2,A3/1.02811D-02,2.99621D-02,1.56146D-02,-4.22464D-03/        
      DATA B0,B1,B2/-3.97070D-01,4.00302D-01,1.06000D+00/                       
      DATA D1,D2,D3,D4/7.01309D-02,1.18520D-02,1.69937D-03,-1.02000D+00/        
      DATA BB1,BB2/-1.71587D-01,2.39219D+00/                                    
      DATA CC1,CC2,CC3,CC4,CC5,CC6                                              
     *    /6.42857D-01,-4.11717D+00,-6.17937D+00,                               
     *     3.08976D-03,8.22994D-02,1.00932D+01/                                 
C                                                                               
      SVTL=REAL(SATVT(T))                                                       
      SVTG=AIMAG(SATVT(T))                                                      
      SVTLD=DBLE(SVTL)                                                          
      SVTGD=DBLE(SVTG)                                                          
      VLN=SVTLD/V0                                                              
      VGN=SVTGD/V0                                                              
      TD=DBLE(T)                                                                
      TN=TD/T0                                                                  
C                                                                               
      DT0=DABS(TN-1.0D+00)+CC4                                                  
      Q=2.0D+00+CC5/DT0**0.6                                                    
      R=Q+1.0D+00                                                               
      IF(TN.GE.1.0D+00) THEN                                                    
      S=1.0D+00/DT0                                                             
      ELSE                                                                      
      S=CC6/DT0**0.6                                                            
      END IF                                                                    
C                                                                               
      RM0=TN**0.5*(A0+A1*TN+A2*TN**2+A3*TN**3)                                  
C                                                                               
C   ***REAL PART-LIQUID FASE***                                                 
C                                                                               
      RMLB=B0+B1/VLN+B2*DEXP(BB1*(1.0D+00/VLN+BB2)**2.0)                        
      XL=CC1*(1.0D+00-1.0D+00/VLN**2.8)                                         
      YL=Q/R*(1.0D+00-1.0D+00/VLN**R)                                           
      ZL=CC2*TN**1.5+CC3*VLN**5.0                                               
      DRML=DEXP(XL)*(D1/TN**10.0+D2)/VLN**1.8                                   
     *    +DEXP(YL)*D3*S/VLN**Q                                                 
     *    +DEXP(ZL)*D4                                                          
C                                                                               
      SRMTLD=RM0+RMLB+DRML                                                      
      SRMTL=SNGL(SRMTLD)                                                        
C                                                                               
C   ***IMAGINALY PART-GASS FASE***                                              
C                                                                               
      RMGB=B0+B1/VGN+B2*DEXP(BB1*(1.0D+00/VGN+BB2)**2.0)                        
      XG=CC1*(1.0D+00-1.0D+00/VGN**2.8)                                         
      YG=Q/R*(1.0D+00-1.0D+00/VGN**R)                                           
      ZG=CC2*TN**1.5+CC3*VGN**5.0                                               
      DRMG=DEXP(XG)*(D1/TN**10.0+D2)/VGN**1.8                                   
     *    +DEXP(YG)*D3*S/VGN**Q                                                 
     *    +DEXP(ZG)*D4                                                          
C                                                                               
      SRMTGD=RM0+RMGB+DRMG                                                      
      SRMTG=SNGL(SRMTGD)                                                        
C                                                                               
C   ***COMPLEX TERMAL CONDUCTIVITY OF SATURATED WATER***                        
C                                                                               
      SATRMT=CMPLX(SRMTL,SRMTG)                                                 
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           THERMAL CONDUCTIVITY OF SATURATED WATER (PRESSURE)                  
C             REAL PART : LIQUID FASE                                           
C             IMAGINALY PART : GASS FASE                                        
C        ** DIMENSION **                                                        
C           THERMAL CONDUCTIVITY :  WATT/METER*KELBIN                           
C           TEMPERATURE : KELDIN                                                
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      COMPLEX FUNCTION  SATRMP(P)                                             
      REAL*4 STEMP,T,P                                                          
      COMPLEX*8 SATRMT                                                          
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      SATRMP=SATRMT(T)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           DYNAMIC VISCOSITY OF SATURATED WATER (TEMPERATURE)                  
C              REAL PART : LIQUID FASE                                          
C              IMAGINALY PART : GASS FASE                                       
C        ** DIMENSION **                                                        
C           DYNAMIC VISCOSITY : PASCAL*SEC                                      
C           TEMPERATURE : KELBIN                                                
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      COMPLEX FUNCTION  SATDVT(T)                                             
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      COMPLEX*8 SATVT                                                           
      REAL*4 T,SVLT,SVTG,SDVTL,SDVTG                                            
      DIMENSION A(4),B(6,5)                                                     
      DATA T0,V0/6.4727D+02,3.14762D-03/                                        
      DATA (A(I),I=1,4)                                                         
     *    /1.81583D-02,1.77624D-02,1.05287D-02,-3.6744D-03/                     
      DATA ((B(I,J),I=1,6),J=1,5)                                               
     *   /5.01938D-01,1.62888D-01,-1.30356D-01,                                 
     *              9.07919D-01,-5.51119D-01,1.46543D-01,                       
     *    2.35622D-01,7.89393D-01,6.73665D-01,                                  
     *              1.207552D+00,6.70665D-02,-8.43370D-02,                      
     *   -2.74637D-01,-7.43539D-01,-9.59456D-01,                                
     *             -6.87343D-01,-4.97089D-01,1.95286D-01,                       
     *    1.45831D-01,2.63129D-01,3.47247D-01,                                  
     *              2.13486D-01,1.00754D-01,-3.2932D-02,                        
     *   -2.70448D-02,-2.53093D-02,-2.67758D-02,                                
     *             -8.22904D-02,6.02253D-02,-2.02595D-02/                       
C                                                                               
      SVTL=REAL(SATVT(T))                                                       
      SVTG=AIMAG(SATVT(T))                                                      
      SVTLD=DBLE(SVTL)                                                          
      SVTGD=DBLE(SVTG)                                                          
      TD=DBLE(T)                                                                
      SPTLD=1.0D+00/SVTLD                                                       
      SPTGD=1.0D+00/SVTGD                                                       
      IF(SPTLD.LE.0.0D+00.OR.SPTLD.GE.1.05D+03) THEN                            
      WRITE(6,6000) SPTLD                                                       
 6000 FORMAT(5X,'SPTLD IS MORE THAN 1050 OR LESS THAN 0'/                       
     *      15X,'SPTLD=',1E12.5)                                                
      GO TO 100                                                                 
      END IF                                                                    
      IF(SPTGD.LE.0.0D+00.OR.SPTGD.GE.1.05D+03) THEN                            
      WRITE(6,6010) SPTGD                                                       
 6010 FORMAT(5X,'SPTGD IS MORE THAN 1050 OR LESS THAN 0'/                       
     *      15X,'SPTGD=',1E12.5)                                                
      GO TO 100                                                                 
      END IF                                                                    
      VLN=SVTLD/V0                                                              
      VGN=SVTGD/V0                                                              
C                                                                               
      TN=TD/T0                                                                  
      P1=0.0D+00                                                                
      DO 10 I=1,4                                                               
      P1=P1+A(I)/TN**(I-1)                                                      
   10 CONTINUE                                                                  
      DV0=TN**0.5/P1                                                            
C                                                                               
C   ***REAL PART-LIQUID FASE***                                                 
C                                                                               
      PL2=0.0D+00                                                               
      DO 20 I=1,6                                                               
      DO 20 J=1,5                                                               
      PL2=PL2+B(I,J)*(1.0D+00/TN-1.0D+00)**(I-1)                                
     *    *(1.0D+00/VLN-1.0D+00)**(J-1)                                         
   20 CONTINUE                                                                  
C                                                                               
      PL3=DV0*DEXP(PL2/VLN)*1.0D-06                                             
      SDVTL=SNGL(PL3)                                                           
C                                                                               
C   ***IMAGINALY PART-GASS FASE***                                              
C                                                                               
      PG2=0.0D+00                                                               
      DO 30 I=1,6                                                               
      DO 30 J=1,5                                                               
      PG2=PG2+B(I,J)*(1.0D+00/TN-1.0D+00)**(I-1)                                
     *    *(1.0D+00/VGN-1.0D+00)**(J-1)                                         
   30 CONTINUE                                                                  
C                                                                               
      PG3=DV0*DEXP(PG2/VGN)*1.0D-06                                             
      SDVTG=SNGL(PG3)                                                           
C                                                                               
C   ***COMPLEX DYNAMIC VISCOCITY OF SATURATED WATER***                          
C                                                                               
      SATDVT=CMPLX(SDVTL,SDVTG)                                                 
C                                                                               
  100 CONTINUE                                                                  
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           DYNAMIC VISCOSITY OF SATURATEDWATER (PRESSURE)                      
C              REAL PART : LIQUID FASE                                          
C              IMAGINALY PART : GASS FASE                                       
C        ** DIMENSION **                                                        
C           DYNAMIC VISCOSITY : PASCAL*SEC                                      
C           TEMPERATURE : KELBIN                                                
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      COMPLEX FUNCTION  SATDVP(P)                                             
      REAL*4 STEMP,T,P                                                          
      COMPLEX*8 SATDVT                                                          
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      SATDVP=SATDVT(T)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           KINETIC VISCOSITY OF SATURATED WATER (TEMPERATURE)                  
C              REAL PART : LIQUID FASE                                          
C              IMAGINALY PART : GASS FASE                                       
C        ** DIMENSION **                                                        
C           KINETIC VISCOSITY : PASCAL*SEC                                      
C           TEMPERATURE : KELBIN                                                
C***********************************************************************        
      COMPLEX FUNCTION  SATKVT(T)                                             
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      COMPLEX*8 SATVT,SATDVT,CV,CDV                                             
      REAL*4 T,SVL,SVG,SDVL,SDVG                                                
C                                                                               
      CV=SATVT(T)                                                               
      SVL=REAL(CV)                                                              
      SVG=AIMAG(CV)                                                             
      SVLD=DBLE(SVL)                                                            
      SVGD=DBLE(SVG)                                                            
C                                                                               
      CDV=SATDVT(T)                                                             
      SDVL=REAL(CDV)                                                            
      SDVG=AIMAG(CDV)                                                           
      SDVLD=DBLE(SDVL)                                                          
      SDVGD=DBLE(SDVG)                                                          
C  * REAL PART *                                                                
      SKVLD=SVLD*SDVLD                                                          
      SKVL=SNGL(SKVLD)                                                          
C  * IMAGINALY PART *                                                           
      SKVGD=SVGD*SDVGD                                                          
      SKVG=SNGL(SKVGD)                                                          
C                                                                               
      SATKVT=CMPLX(SKVL,SKVG)                                                   
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C      FUNCTION                                                                 
C           KINETIC VISCOSITY OF SATURATED WATER (PRESSURE)                     
C              REAL PART : LIQUID FASE                                          
C              IMAGINALY PART : GASS FASE                                       
C        ** DIMENSION **                                                        
C           KINETIC VISCOSITY : PASCAL*SEC                                      
C           PRESSURE : PASCAL                                                   
C***********************************************************************        
      COMPLEX FUNCTION  SATKVP(P)                                             
      REAL*4 STEMP,T,P                                                          
      COMPLEX*8 SATKVT                                                          
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      SATKVP=SATKVT(T)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION LAPLACE COEFFICIENT OF SATURATED WATER (TEMPERATURE)            
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            LAPLACE COEFFICIENT : METER                                        
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION SATLAT(T)                                                 
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 VLTS,VGTS,SIGMA,T                                                  
      COMPLEX*8 SATVT,VTS                                                       
C                                                                               
      DATA GC/9.80665D+00/                                                      
C                                                                               
      VTS=SATVT(T)                                                              
      VLTS=REAL(VTS)                                                            
      VGTS=AIMAG(VTS)                                                           
      VLTSD=DBLE(VLTS)                                                          
      VGTSD=DBLE(VGTS)                                                          
      SGMTD=DBLE(SIGMA(T))                                                      
C                                                                               
      PLG=VLTSD*VGTSD/(VGTSD-VLTSD)                                             
      SLATD=DSQRT(SGMTD*PLG/GC)                                                 
      SATLAT=SNGL(SLATD)                                                        
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION LAPLACE COEFFICIENT OF SATURATED WATER (PRESSURE)               
C        *DIMENSIONS*                                                           
C            PRESSURE : PASCAL                                                  
C            LAPLACE COEFFICIENT : METER                                        
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION SATLAP(P)                                                 
      REAL*4 T,P,STEMP,SATLAT                                                   
C                                                                               
      T=STEMP(P)                                                                
      SATLAP=SATLAT(T)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION PRANTL NUMBER (TEMPERATURE,PRESSURE)                            
C        * DIMENSION *                                                          
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION PRTP(T,P)                                                 
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,P,DHTPTP,DVTP,RMTP                                               
C                                                                               
C                                                                               
      CPTPD=DBLE(DHTPTP(T,P))                                                   
      DVTPD=DBLE(DVTP(T,P))                                                     
      RMTPD=DBLE(RMTP(T,P))                                                     
C                                                                               
      PRTPD=CPTPD*DVTPD/RMTPD                                                   
C                                                                               
      PRTP=SNGL(PRTPD)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION PRANTL NUMBER OF SATURATED WATER (TEMPERATURE)                  
C         * DIMENSIONS *                                                        
C            TEMPERATURE : KELBIN                                               
C      COMPLEX FUNCTION SATPRT                                                  
C         REAL PART : LIQUID FASE                                               
C         IMAGINALY PART : GASS FASE                                            
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SATPRT(T)                                              
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      COMPLEX*8 SDHTPT,SATDVT,SATRMT,CPTS,DVTS,RMTS                             
      REAL*4 CPGTS,CPLTS,DVGTS,DVLTS,RMGTS,RMLTS,PRGTS,PRLTS,T                  
C                                                                               
      CPTS=SDHTPT(T)                                                            
      DVTS=SATDVT(T)                                                            
      RMTS=SATRMT(T)                                                            
C                                                                               
      CPLTS=REAL(CPTS)                                                          
      CPGTS=AIMAG(CPTS)                                                         
      DVLTS=REAL(DVTS)                                                          
      DVGTS=AIMAG(DVTS)                                                         
      RMLTS=REAL(RMTS)                                                          
      RMGTS=AIMAG(RMTS)                                                         
C                                                                               
      CPLTSD=DBLE(CPLTS)                                                        
      CPGTSD=DBLE(CPGTS)                                                        
      DVLTSD=DBLE(DVLTS)                                                        
      DVGTSD=DBLE(DVGTS)                                                        
      RMLTSD=DBLE(RMLTS)                                                        
      RMGTSD=DBLE(RMGTS)                                                        
C   ***REAL PART : LIQUID FASE***                                               
      PRLTSD=CPLTSD*DVLTSD/RMLTSD                                               
C   ***IMAGINALY PART : GASS FASE***                                            
      PRGTSD=CPGTSD*DVGTSD/RMGTSD                                               
C                                                                               
      PRLTS=SNGL(PRLTSD)                                                        
      PRGTS=SNGL(PRGTSD)                                                        
C                                                                               
      SATPRT=CMPLX(PRLTS,PRGTS)                                                 
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION PRANTL NUMBER OF SATURATED WATER (PRESSURE)                     
C        * DIMENSIONS *                                                         
C            PRESSURE : PASCAL                                                  
C      COMPLEX FUNCTION SATPRP                                                  
C         REAL PART : LIQUID FASE                                               
C         IMAGINALY PART : GASS FASE                                            
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SATPRP(P)                                              
      REAL*4 T,P,STEMP                                                          
      COMPLEX*8 SATPRT                                                          
C                                                                               
      T=STEMP(P)                                                                
      SATPRP=SATPRT(T)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION YACOB NUMBER OF WATER (TEMPERATURE,PRSSURE)                     
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C            YACOB NUMBER : NON-DIMENSION                                       
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION YATP(T,P)                                                 
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 DHTPTP,CP,HLTS,HGTS,TS,STEMP,T,P                                   
      COMPLEX*8 SATHT,CH                                                        
      DATA PC/22.12E+06/                                                        
C                                                                               
      IF(P .GT. PC) THEN                                                        
        WRITE(6,6000) P                                                         
 6000   FORMAT(1H1,5X,'(FUNC.YATP) P IS GREATER THAN 22.12 MEGA-PASCAL'/        
     *         1H ,5X,'P=',1PE12.5)                                             
        YATP=0.0E+00                                                            
        RETURN                                                                  
      END IF                                                                    
C                                                                               
      CP=DHTPTP(T,P)                                                            
      CPD=DBLE(CP)                                                              
C                                                                               
      TS=STEMP(P)                                                               
      TSD=DBLE(TS)                                                              
      TD=DBLE(T)                                                                
      DELTTS=DABS(TSD-TD)                                                       
C                                                                               
      CH=SATHT(TS)                                                              
      HLTS=REAL(CH)                                                             
      HGTS=AIMAG(CH)                                                            
      HLGD=DBLE(HGTS)-DBLE(HLTS)                                                
C                                                                               
      YAD=CPD*DELTTS/HLGD                                                       
      YATP=SNGL(YAD)                                                            
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION NUSSELT NUMBER (TEMPERATURE,PRESSURE,                           
C                   HYDRAULIC DIAMATER,HEAT TRANSFER COEFFICIENT)               
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMATER : METER                                         
C            HEAT TRANSFER COEFFICIENT : WATT PER KELBIN                        
C            NUSSELT NUMBER : NON-DIMENSION                                     
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION ANUTP(T,P,DH,ALPHA)                                       
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 RMTP,T,P,DH,ALPHA                                                  
C                                                                               
      RMTPD=DBLE(RMTP(T,P))                                                     
      DHD=DBLE(DH)                                                              
      ALPD=DBLE(ALPHA)                                                          
C                                                                               
      ANUTPD=ALPD*DHD/RMTPD                                                     
      ANUTP=SNGL(ANUTPD)                                                        
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION NUSSELT NUMBER OF SATURATED WATER                               
C      (TEMPERATURE,HYDRAULIC DIAMATER,HEAT TRANSFER COEFFICIENT)               
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            HYDRAULIC DIAMATER : METER                                         
C            HEAT TRANSFER COEFFICIENT : WATT PER KELBIN                        
C            NUSSELT NUMBER : NON-DIMENSION                                     
C      COMPLEX FUNCTION SNUT(T,DH,ALPHA)                                        
C         REAL PART : NUSSELT NUMBER OF SATURATED LIQUID PHASE                  
C         IMAGINALY PART :NUSSELT NUMBER OF SATURATED GASS PHASE                
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SNUT(T,DH,ALPHA)                                       
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 RMLTS,RMGTS,DH,ALPHA,SNULT,SNUGT,T                                 
      COMPLEX*8 SATRMT,RMTS                                                     
C                                                                               
      RMTS=SATRMT(T)                                                            
      RMLTS=REAL(RMTS)                                                          
      RMGTS=AIMAG(RMTS)                                                         
      RMLTSD=DBLE(RMLTS)                                                        
      RMGTSD=DBLE(RMGTS)                                                        
      DHD=DBLE(DH)                                                              
      ALPD=DBLE(ALPHA)                                                          
C   *REAL PART-LIQUID PHASE*                                                    
      SNULTD=ALPD*DHD/RMLTSD                                                    
      SNULT=SNGL(SNULTD)                                                        
C   *IMAGINALY PART-GASS PHASE*                                                 
      SNUGTD=ALPD*DHD/RMGTSD                                                    
      SNUGT=SNGL(SNUGTD)                                                        
C                                                                               
      SNUT=CMPLX(SNULT,SNUGT)                                                   
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION NUSSELT NUMBER OF SATURATED WATER                               
C      (PRESSURE,HYDRAULIC DIAMATER,HEAT TRANSFER COEFFICIENT)                  
C        *DIMENSIONS*                                                           
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMATER : METER                                         
C            HEAT TRANSFER COEFFICIENT : WATT PER KELBIN                        
C            NUSSELT NUMBER : NON-DIMENSION                                     
C      COMPLEX FUNCTION SNUT(T,DH,ALPHA)                                        
C         REAL PART : NUSSELT NUMBER OF SATURATED LIQUID PHASE                  
C         IMAGINALY PART :NUSSELT NUMBER OF SATURATED GASS PHASE                
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SNUP(P,DH,ALPHA)                                       
      REAL*4 T,P,DH,ALPHA,STEMP                                                 
      COMPLEX*8 SNUT                                                            
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      SNUP=SNUT(T,DH,ALPHA)                                                     
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION HEAT TRANSFER COEFFICIENT (TEMPERATURE,PRESSURE,                
C                   HYDRAULIC DIAMATER,NUSSELT NUMBER)                          
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMATER : METER                                         
C            NUSSELT NUMBER : NON-DIMENSION                                     
C            HEAT TRANSFER COEFFICIENT : WATT PER KELBIN                        
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION ALPTPN(T,P,DH,NU)                                         
      IMPLICIT REAL*8 (A-Z)                                                     
      REAL*4 RMTP,T,P,DH,NU                                                     
C                                                                               
      RMTPD=DBLE(RMTP(T,P))                                                     
      DHD=DBLE(DH)                                                              
      NUD=DBLE(NU)                                                              
C                                                                               
      ALPD=NUD*RMTPD/DHD                                                        
      ALPTPN=SNGL(ALPD)                                                         
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION HEAT TRANSFER COEFFICIENT OF SATURATED WATER                    
C      (TEMPERATURE,HYDRAULIC DIAMATER,NUSSELT NUMBER)                          
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            HYDRAULIC DIAMATER : METER                                         
C            NUSSELT NUMBER : NON-DIMENSION                                     
C            HEAT TRANSFER COEFFICIENT : WATT KELBIN                            
C      COMPLEX FUNCTION SALPTN(T,DH,NU)                                         
C         REAL PART : LIQUID PHASE                                              
C         IMAGINALY PART : GASS PHASE                                           
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SALPTN(T,DH,NU)                                        
      IMPLICIT REAL*8 (A-Z)                                                     
      REAL*4 RMLTS,RMGTS,DH,NU,SALPLT,SALPGT,T                                  
      COMPLEX*8 SATRMT,RMTS                                                     
C                                                                               
      RMTS=SATRMT(T)                                                            
      RMLTS=REAL(RMTS)                                                          
      RMGTS=AIMAG(RMTS)                                                         
      RMLTSD=DBLE(RMLTS)                                                        
      RMGTSD=DBLE(RMGTS)                                                        
      DHD=DBLE(DH)                                                              
      NUD=DBLE(NU)                                                              
C    *REAL PART-LIQUID PHASE*                                                   
      SALPLD=NUD*RMLTSD/DHD                                                     
      SALPLT=SNGL(SALPLD)                                                       
C    *IMAGINALY PART-GASS PHASE*                                                
      SALPGD=NUD*RMGTSD/DHD                                                     
      SALPGT=SNGL(SALPGD)                                                       
C                                                                               
      SALPTN=CMPLX(SALPLT,SALPGT)                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION HEAT TRANSFER COEFFICIENT OF SATURATED WATER                    
C      (PRESSUREE,HYDRAULIC DIAMATER,NUSSELT NUMBER)                            
C        *DIMENSIONS*                                                           
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMATER : METER                                         
C            NUSSELT NUMBER : NON-DIMENSION                                     
C            HEAT TRANSFER COEFFICIENT : WATT KELBIN                            
C      COMPLEX FUNCTION SALPPN(P,DH,NU)                                         
C         REAL PART : LIQUID PHASE                                              
C         IMAGINALY PART : GASS PHASE                                           
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SALPPN(P,DH,NU)                                        
      REAL*4 T,P,DH,NU,STEMP                                                    
      COMPLEX*8 SALPTN                                                          
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      SALPPN=SALPTN(T,DH,NU)                                                    
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION BOILING NUMBER (TEMPERATURE,HEAT FLUX,HASS FLOW)                
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            HEAT FLUX : WATT PER SQUARE METER                                  
C            MASS FLOW : KIL0-GRAM PER SECOND                                   
C            BOILING NUMBER : NON DIMENSION                                     
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION BOT(T,QW,AH,G)                                            
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 HLTS,HGTS,T,QW,AH,G,HLG                                            
      COMPLEX*8 SATHT,HTS                                                       
C                                                                               
      HTS=SATHT(T)                                                              
      HLTS=REAL(HTS)                                                            
      HGTS=AIMAG(HTS)                                                           
      HLG=HGTS-HLTS                                                             
      HLGD=DBLE(HLG)                                                            
      QWD=DBLE(QW)                                                              
      AHD=DBLE(AH)                                                              
      GD=DBLE(G)                                                                
C                                                                               
      BOTD=QWD*AHD/(GD*HLGD)                                                    
      BOT=SNGL(BOTD)                                                            
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION BOILING NUMBER (PRESSURE,HEAT FLUX,HASS FLOW)                   
C        *DIMENSIONS*                                                           
C            PRESSURE : PASCAL                                                  
C            HEAT FLUX : WATT PER SQUARE METER                                  
C            MASS FLOW : KIL0-GRAM PER SECOND                                   
C            BOILING NUMBER : NON DIMENSION                                     
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION BOP(P,QW,AH,G)                                            
      REAL*4 T,P,QW,AH,G,STEMP,BOT                                              
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      BOP=BOT(T,QW,AH,G)                                                        
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION REYNOLDS NUMBER (TEMPERATURE,PRESSURE,                          
C                         HYDRAULIC DIAMETER,VELOCITY)                          
C        * DIMENSIONS *                                                         
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMETER : METER                                         
C            VELOCITY : METER PER SECOND                                        
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION RETPU(T,P,DH,U)                                           
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 DVTP,VTP,T,P,DH,U                                                  
C                                                                               
      DVTPD=DBLE(DVTP(T,P))                                                     
      VTPD=DBLE(VTP(T,P))                                                       
      UD=DBLE(U)                                                                
      DHD=DBLE(DH)                                                              
C                                                                               
      RETPD=UD*DHD/(VTPD*DVTPD)                                                 
C                                                                               
      RETPU=SNGL(RETPD)                                                         
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION REYNOLDS NUMBER (TEMPERATURE,PRESSURE,                          
C                        FLOW AREA,HYDRAULIC DIAMETER,MASS FLOW)                
C        * DIMENSIONS *                                                         
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C            FLOW AREA : SQUARE METER                                           
C            HYDRAULIC DIAMETER : METER                                         
C            MASS FLOW : KILO-GRAM PER SECOND                                   
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION RETPG(T,P,AH,DH,G)                                        
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 DVTP,T,P,AH,DH,G                                                   
C                                                                               
      DVTPD=DBLE(DVTP(T,P))                                                     
      GD=DBLE(G)                                                                
      DHD=DBLE(DH)                                                              
      AHD=DBLE(AH)                                                              
C                                                                               
      RETPD=GD*DHD/(AHD*DVTPD)                                                  
C                                                                               
      RETPG=SNGL(RETPD)                                                         
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION REYNOLDS NUMBER OF SATURATED WATER                              
C     (TEMPERATURE,HYDRAULIC DIAMATER,LIQUID VELOCITY)                          
C        *DIMENSIONS*                                                           
C            TEMPARATURE ; KELBIN                                               
C            HYDRAULIC DIAMETER : METER                                         
C            LIQUID VELOCITY : METER PER SECOND                                 
C            REYNOLDS NUMBER : NON-DIMENSION                                    
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION SRELTU(T,DH,UL)                                           
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,DH,UL,SDVL,SVL                                                   
