C            TEMPERATURE : KELBIN                                               
C            FLOW AREA : SQUARE METER                                           
C            HYDRAULIC DIAMETER : METER                                         
C            MASS FLOW : KILO-GRAM PER SECOND                                   
C            STEAM QUALITY : NON-DIMENSION                                      
C            REYNOLDS NUMBER : NON-DIMENSION                                    
C      COMPLEX FUNCTION SREOTG(T,AH,DH,G,X)                                     
C         REAL PART : REYNOLDS NUMBER REFER TO THE LIQUID PHASE MASS            
C                     FLOW AS LIQUID ONLY. IE. GT=GL=(1-X)*G                    
C         IMAGINALY PART : REYNOLDS NUMBER REFER TO THE GASS PHASE MASS         
C                     FLOW AS GASS ONLY IE. GT=GG=X*G                           
C         THIS IS A REYNOLDS NUMBER EVALUATED SUPERFICIAL VELOCITY              
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SREOTG(T,AH,DH,G,X)                                    
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 DVLTS,DVGTS,RELTS,REGTS,T,AH,DH,G,X                                
      COMPLEX*8 SATDVT,DVTS                                                     
C                                                                               
      DVTS=SATDVT(T)                                                            
      DVLTS=REAL(DVTS)                                                          
      DVGTS=AIMAG(DVTS)                                                         
      DVLTSD=DBLE(DVLTS)                                                        
      DVGTSD=DBLE(DVGTS)                                                        
      AHD=DBLE(AH)                                                              
      DHD=DBLE(DH)                                                              
      GD=DBLE(G)                                                                
      XD=DBLE(X)                                                                
C   ***REAL PART  ***                                                           
      RELTSD=GD*(1.0D+00-XD)*DHD/(AHD*DVLTSD)                                   
      RELTS=SNGL(RELTSD)                                                        
C   ***IMAGINALY PART  ***                                                      
      REGTSD=GD*XD*DHD/(AHD*DVGTSD)                                             
      REGTS=SNGL(REGTSD)                                                        
C                                                                               
      SREOTG=CMPLX(RELTS,REGTS)                                                 
C                                                                               
      RETURN                                                                    
      END                                                                       
