C            TEMPERATURE : KELBIN                                               
C            FLOW AREA : SQUARE METER                                           
C            HYDRAULIC DIAMETER : METER                                         
C            MASS FLOW : KILO-GRAM PER SECOND                                   
C            REYNOLDS NUMBER : NON-DIMENSION                                    
C                                                                               
C      COMPLEX FUNCTION SREETG(T,AH,DH,G)                                       
C         REAL PART : REYNOLDS NUMBER REFER TO THE TOTAL MASS                   
C                     FLOW AS LIQUID ONLY. IE. GT=GL=G                          
C         IMAGINALY PART : REYNOLDS NUMBER REFER TO THE TOTAL MASS              
C                     FLOW AS GASS ONLY IE. GT=GG=G                             
C         THIS IS A REYNOLDS NUMBER EVALUATED SINGLE PHASE EQUIVALIUM           
C         VELOCITY                                                              
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SREETG(T,AH,DH,G)                                      
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 DVLTS,DVGTS,RELETS,REGETS,T,AH,DH,G                                
      COMPLEX*8 SATDVT,DVTS                                                     
C                                                                               
      DVTS=SATDVT(T)                                                            
      DVLTS=REAL(DVTS)                                                          
      DVGTS=AIMAG(DVTS)                                                         
      DVLTSD=DBLE(DVLTS)                                                        
      DVGTSD=DBLE(DVGTS)                                                        
      AHD=DBLE(AH)                                                              
      DHD=DBLE(DH)                                                              
      GD=DBLE(G)                                                                
C   ***REAL PART ***                                                            
      RELED=GD*DHD/(AHD*DVLTSD)                                                 
      RELETS=SNGL(RELED)                                                        
C   ***IMAGINALY PART ***                                                       
      REGED=GD*DHD/(AHD*DVGTSD)                                                 
      REGETS=SNGL(REGED)                                                        
C                                                                               
      SREETG=CMPLX(RELETS,REGETS)                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
