C***********************************************************************        
C                                                                               
C      FUNCTION FROUDE NUMBER OF SATURATED TWO PHASE WATER                      
C     (TEMPERATURE,FLOW AREA,HYDRAULIC DIAMATER,MASS FLOW,STEAM QUALITY)        
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            FLOW AREA : SQUARE METER                                           
C            HYDRAULIC DIAMATER : METER                                         
C            MASS FLOW : KILO-GRAM PER SECOND                                   
C            STEAM QUALITY : NON-DIMENSION                                      
C            FROUDE NUMBER : NON-DIMENSION                                      
C      COMPLEX FUNCTION SFROTG(T,AH,DH,G,X)                                     
C         REAL PART : FROUDE NUMBER REFER TO THE LIQUID PHASE MASS              
C                     FLOW AS LIQUID ONLY. IE. GT=GL=(1-X)*G                    
C         IMAGINALY PART : FROUD NUMBER REFER TO THE GASS PHASE MASS            
C                     FLOW AS GASS ONLY. IE. GT=GG=X*G                          
C         GC : STANDARD ACCELERATION OF GRAVITY                                 
C      THIS IS A FROUD NUMBER EVALUATED BY SUPERFICIAL VELOCITY                 
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SFROTG(T,AH,DH,G,X)                                    
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 VLTS,VGTS,T,AH,DH,G,X,SFRLOT,SFRGOT                                
      COMPLEX*8 SATVT,VTS                                                       
C                                                                               
      DATA GC/9.80665D+00/                                                      
C                                                                               
      VTS=SATVT(T)                                                              
      VLTS=REAL(VTS)                                                            
      VGTS=AIMAG(VTS)                                                           
      VLTSD=DBLE(VLTS)                                                          
      VGTSD=DBLE(VGTS)                                                          
      AHD=DBLE(AH)                                                              
      DHD=DBLE(DH)                                                              
      GD=DBLE(G)                                                                
      XD=DBLE(X)                                                                
C    *SUPERFICIAL VELOCITY OF LIQUID*                                           
      ULO=(1.0D+00-XD)*GD*VLTSD/AHD                                             
C    *SUPERFICIAL VELOCITY OF GASS*                                             
      UGO=XD*GD*VGTSD/AHD                                                       
C    *FROUDE NUMBER OF LIQUIDE*                                                 
      FRLO=ULO**2.0/(GC*DHD)                                                    
      SFRLOT=SNGL(FRLO)                                                         
C    *FROUDE NUMBER OF GASS*                                                    
      FRGO=UGO**2.0/(GC*DHD)                                                    
      SFRGOT=SNGL(FRGO)                                                         
C                                                                               
      SFROTG=CMPLX(SFRLOT,SFRGOT)                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION FROUDE NUMBER OF SATURATED TWO PHASE WATER                      
C     (PRESSURE,FLOW AREA,HYDRAULIC DIAMATER,MASS FLOW,STEAM QUALITY)           
C        *DIMENSIONS*                                                           
C            PRESSURE : PASCAL                                                  
C            FLOW AREA : SQUARE METER                                           
C            HYDRAULIC DIAMATER : METER                                         
C            MASS FLOW : KILO-GRAM PER SECOND                                   
