C            TEMPERATURE : KELBIN                                               
C            FLOW AREA : SQUARE METER                                           
C            HYDRAULIC DIAMATER : METER                                         
C            MASS FLOW : KILO-GRAM PER SECOND                                   
C            FROUDE NUMBER : NON-DIMENSION                                      
C      COMPLEX FUNCTION SFRETG(T,AH,DH,G)                                       
C         REAL PART : FROUDE NUMBER REFER TO THE TOTAL MASS                     
C                     FLOW AS LIQUID ONLY. IE. GT=GL=G                          
C         IMAGINALY PART : FROUD NUMBER REFER TO THE TOTAL MASS                 
C                     FLOW AS GASS ONLY. IE. GT=GG=G                            
C         GC : STANDARD ACCELERATION OF GRAVITY                                 
C      THIS IS A FROUD NUMBER EVALUATED BY SINGLE PHASE EQUIVALIUM              
C      VELOCITY                                                                 
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SFRETG(T,AH,DH,G)                                      
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 VLTS,VGTS,T,AH,DH,G,SFRLOT,SFRGOT                                  
      COMPLEX*8 SATVT,VTS                                                       
C                                                                               
      DATA GC/9.80665D+00/                                                      
C                                                                               
      VTS=SATVT(T)                                                              
      VLTS=REAL(VTS)                                                            
      VGTS=AIMAG(VTS)                                                           
      VLTSD=DBLE(VLTS)                                                          
      VGTSD=DBLE(VGTS)                                                          
      AHD=DBLE(AH)                                                              
      DHD=DBLE(DH)                                                              
      GD=DBLE(G)                                                                
C    *LIQUID PHASE EQUIVALIUM VELOCITY *                                        
      ULE=GD*VLTSD/AHD                                                          
C    *GASS PHASE EQUIVALIUM VELOCITY *                                          
      UGE=GD*VGTSD/AHD                                                          
C    *FROUDE NUMBER OF LIQUIDE*                                                 
      FRLE=ULE**2.0/(GC*DHD)                                                    
      SFRLET=SNGL(FRLE)                                                         
C    *FROUDE NUMBER OF GASS*                                                    
      FRGE=UGE**2.0/(GC*DHD)                                                    
      SFRGET=SNGL(FRGE)                                                         
C                                                                               
      SFRETG=CMPLX(SFRLET,SFRGET)                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
