      REAL*4 VLTS,VGTS,SIGMA,T,DH                                               
      COMPLEX*8 SATVT,VTS                                                       
C                                                                               
      DATA GC/9.80665D+00/                                                      
C                                                                               
      VTS=SATVT(T)                                                              
      VLTS=REAL(VTS)                                                            
      VGTS=AIMAG(VTS)                                                           
      VLTSD=DBLE(VLTS)                                                          
      VGTSD=DBLE(VGTS)                                                          
      SGMTD=DBLE(SIGMA(T))                                                      
      DHD=DBLE(DH)                                                              
C                                                                               
      PLG=VLTSD*VGTSD/(VGTSD-VLTSD)                                             
      SEOTD=GC*DHD**2.0/(SGMTD*PLG)                                             
      SEOT=SNGL(SEOTD)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
