C***********************************************************************        
C                                                                               
C      FUNCTION GRASHOF NUMBER OF WATER                                         
C      (TEMPERATURE,PRESSURE,HYDRAULIC DIAMETER,WALL TEMPERATURE)               
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMETER : METER                                         
C            WALL TEMPERATURE : KELBIN                                          
C            GRASHOF NUMBER : NON-DIMENSION                                     
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION GRTP(T,P,DH,TW)                                           
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,P,DH,TW,VTP,DVTP,ALFA                                            
      DATA GC/9.80665D+00/                                                      
C                                                                               
      VTPD=DBLE(VTP(T,P))                                                       
      DVTPD=DBLE(DVTP(T,P))                                                     
      BETAD=DBLE(ALFA(T,P))                                                     
      DHD=DBLE(DH)                                                              
      TD=DBLE(T)                                                                
      TWD=DBLE(TW)                                                              
C                                                                               
      GRTPD=GC*BETAD*DABS(TD-TWD)*DHD**3.0/(VTPD*DVTPD)**2.0                    
C                                                                               
      GRTP=SNGL(GRTPD)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION GRASHOF NUMBER OF SATURATED WATER                               
C      (TEMPERATURE,HYDRAULIC DIAMETER,WALL TEMPERATURE)                        
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            HYDRAULIC DIAMETER : METER                                         
C            WALL TEMPERATURE : KELBIN                                          
C            GRASHOF NUMBER : NON-DIMENSION                                     
C        *REMARKS*                                                              
C            REAL PART : GRASHOF NUMBER OF SATURATED WATER                      
C            IMAGINALY PART : GRASHOF NUMBER OF SATURATED STERM                 
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SGRET(T,DH,TW)                                         
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,DH,TW,SVL,SVG,SDVL,SDVG,SBETAL,SBETAG                            
      COMPLEX*8 SATVT,SATDVT,SALFAT,CV,CDV,CALFA                                
      DATA GC/9.80665D+00/                                                      
C                                                                               
      CV=SATVT(T)                                                               
      SVL=REAL(CV)                                                              
      SVG=AIMAG(CV)                                                             
      SVLD=DBLE(SVL)                                                            
      SVGD=DBLE(SVG)                                                            
C                                                                               
      CDV=SATDVT(T)                                                             
      SDVL=REAL(CDV)                                                            
      SDVG=AIMAG(CDV)                                                           
      SDVLD=DBLE(SDVL)                                                          
      SDVGD=DBLE(SDVG)                                                          
C                                                                               
      CALFA=SALFAT(T)                                                           
      SBETAL=REAL(CALFA)                                                        
      SBETAG=AIMAG(CALFA)                                                       
      SBLD=DBLE(SBETAL)                                                         
      SBGD=DBLE(SBETAG)                                                         
C                                                                               
      DHD=DBLE(DH)                                                              
      TD=DBLE(T)                                                                
      TWD=DBLE(TW)                                                              
C    * REAL PART *                                                              
      GRLTD=GC*SBLD*DABS(TD-TWD)*DHD**3.0/(SVLD*SDVLD)**2.0                     
      GRL=SNGL(GRLTD)                                                           
C    * IMAGINALY PART *                                                         
      GRGTD=GC*SBGD*DABS(TD-TWD)*DHD**3.0/(SVGD*SDVGD)**2.0                     
      GRG=SNGL(GRGTD)                                                           
C                                                                               
      SGRET=CMPLX(GRL,GRG)                                                      
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION GRASHOF NUMBER OF SATURATED WATER                               
C      (PRESSURE,HYDRAULIC DIAMETER,WALL TEMPERATURE)                           
C        *DIMENSIONS*                                                           
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMETER : METER                                         
C            WALL TEMPERATURE : KELBIN                                          
C            GRASHOF NUMBER : NON-DIMENSION                                     
C        *REMARKS*                                                              
C            REAL PART : GRASHOF NUMBER OF SATURATED WATER                      
C            IMAGINALY PART : GRASHOF NUMBER OF SATURATED STERM                 
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SGREP(P,DH,TW)                                         
      REAL*4 STEMP,T,P,DH,TW                                                    
      COMPLEX*8 SGRET                                                           
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      SGREP=SGRET(T,DH,TW)                                                      
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION GRASHOF NUMBER OF SATURATED TWO PHASE WATER                     
C      (TEMPERATURE,HYDRAULIC DIAMETER)                                         
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            HYDRAULIC DIAMETER : METER                                         
C            GRASHOF NUMBER : NON-DIMENSION                                     
C        *REMARKS*                                                              
C            REAL PART : GRASHOF NUMBER OF SATURATED TWO PHASE                  
C                        WATER OF CONDENSATION STATE                            
C            IMAGINALY PART : GRASHOF NUMBER OF SATURATED TWO PHASE             
C                        WATER OF BOILING STATE                                 
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SGROT(T,DH)                                            
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,DH,SVL,SVG,SDVL,SDVG                                             
      COMPLEX*8 SATVT,SATDVT,CV,CDV                                             
      DATA GC/9.80665D+00/                                                      
C                                                                               
      CV=SATVT(T)                                                               
      SVL=REAL(CV)                                                              
      SVG=AIMAG(CV)                                                             
      SVLD=DBLE(SVL)                                                            
      SVGD=DBLE(SVG)                                                            
C                                                                               
      CDV=SATDVT(T)                                                             
      SDVL=REAL(CDV)                                                            
      SDVG=AIMAG(CDV)                                                           
      SDVLD=DBLE(SDVL)                                                          
      SDVGD=DBLE(SDVG)                                                          
C                                                                               
      DHD=DBLE(DH)                                                              
C     * REAL PART *                                                             
      SGROLD=GC*DHD**3.0*(1.0D+00-SVLD/SVGD)/(SVLD*SDVLD)**2.0                  
      SGROL=SNGL(SGROLD)                                                        
C     * IMAGINALY PART *                                                        
      SGROGD=GC*DHD**3.0*(SVGD/SVLD-1.0D+00)/(SVGD*SDVGD)**2.0                  
      SGROG=SNGL(SGROGD)                                                        
C                                                                               
      SGROT=CMPLX(SGROL,SGROG)                                                  
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION GRASHOF NUMBER OF SATURATED TWO PHASE WATER                     
C      (PRESSURE,HYDRAULIC DIAMETER)                                            
C        *DIMENSIONS*                                                           
C            PRESSURE : KELBIN                                                  
C            HYDRAULIC DIAMETER : METER                                         
C            GRASHOF NUMBER : NON-DIMENSION                                     
C        *REMARKS*                                                              
C            REAL PART : GRASHOF NUMBER OF SATURATED TWO PHASE                  
C                        WATER OF CONDENSATION STATE                            
C            IMAGINALY PART : GRASHOF NUMBER OF SATURATED TWO PHASE             
C                        WATER OF BOILING STATE                                 
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SGROP(P,DH)                                            
      REAL*4 STEMP,T,P,DH                                                       
      COMPLEX*8 SGROT                                                           
C                                                                               
      T=STEMP(P)                                                                
C                                                                               
      SGROP=SGROT(T,DH)                                                         
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION GALILEO NUMBER OF WATER                                         
C      (TEMPERATURE,PRESSURE,HYDRAULIC DIAMETER)                                
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            PRESSURE : PASCAL                                                  
C            HYDRAULIC DIAMETER : METER                                         
C            GALILEO NUMBER : NON-DIMENSION                                     
C                                                                               
C***********************************************************************        
      REAL*4 FUNCTION GATP(T,P,DH)                                              
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,P,DH,VTP,DVTP                                                    
      DATA GC/9.80665D+00/                                                      
C                                                                               
      VTPD=DBLE(VTP(T,P))                                                       
      DVTPD=DBLE(DVTP(T,P))                                                     
      DHD=DBLE(DH)                                                              
C                                                                               
      GATPD=GC*DHD**3.0/(VTPD*DVTPD)**2.0                                       
C                                                                               
      GATP=SNGL(GATPD)                                                          
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION GALILEO NUMBER OF SATURATED  WATER                              
C      (TEMPERATURE,HYDRAULIC DIAMETER)                                         
C        *DIMENSIONS*                                                           
C            TEMPERATURE : KELBIN                                               
C            HYDRAULIC DIAMETER : METER                                         
C            GALILEO NUMBER : NON-DIMENSION                                     
C        *REMARKS*                                                              
C            REAL PART : GALILEO NUMBER OF SATURATED WATER                      
C            IMAGINALY PART : GALILEO NUMBER OF SATURATED STEAM                 
C                                                                               
C***********************************************************************        
      COMPLEX FUNCTION SGAT(T,DH)                                             
      IMPLICIT REAL*8 (A-H,O-Z)                                                 
      REAL*4 T,DH,SVL,SVG,SDVL,SDVG                                             
      COMPLEX*8 SATVT,SATDVT,CV,CDV                                             
      DATA GC/9.80665D+00/                                                      
C                                                                               
      CV=SATVT(T)                                                               
      SVL=REAL(CV)                                                              
      SVG=AIMAG(CV)                                                             
      SVLD=DBLE(SVL)                                                            
      SVGD=DBLE(SVG)                                                            
C                                                                               
      CDV=SATDVT(T)                                                             
      SDVL=REAL(CDV)                                                            
      SDVG=AIMAG(CDV)                                                           
      SDVLD=DBLE(SDVL)                                                          
      SDVGD=DBLE(SDVG)                                                          
C                                                                               
      DHD=DBLE(DH)                                                              
C     * REAL PART *                                                             
      SGALTD=GC*DHD**3.0/(SVLD*SDVLD)**2.0                                      
      SGAL=SNGL(SGALTD)                                                         
C     * IMAGINALY PART *                                                        
      SGAGTD=GC*DHD**3.0/(SVGD*SDVGD)**2.0                                      
      SGAG=SNGL(SGAGTD)                                                         
C                                                                               
      SGAT=CMPLX(SGAL,SGAG)                                                     
C                                                                               
      RETURN                                                                    
      END                                                                       
C***********************************************************************        
C                                                                               
C      FUNCTION GALILEO NUMBER OF SATURATED WATER                               
C      (PRESSURE,HYDRAULIC DIAMETER)                                            
