     * / 1.0   E+00 , 2.389 E-01 , 5.779 E+01 , 6.935 E+02 , 6.242 E+12/        
      DATA (EXCH(16,I),I=1,6)                                                   
     * / 1.0   E+00 , 1.0   E-02 , 1.0   E-03 , 6.720 E-04 , 2.419 E+00,        
     *   2.089 E-05 /                                                           
      DATA (EXCH(17,I),I=1,5)                                                   
     * / 1.0   E+00 , 1.6021E-19 , 4.4503E-23 , 1.6604E-24 , 1.6021E-12/        
      DATA (EXCH(18,I),I=1,4)                                                   
     * / 1.0   E+00 , 2.389 E-01 , 8.6   E+03 , 1.761 E+03 /                    
C                                                                               
C                                                                               
C                                                                               
      IF(N1.EQ.19) GO TO 50                                                     
C                                                                               
C                                                                               
C                                                                               
      CONVFC=EXCH(N1,N3)/EXCH(N1,N2)                                            
      VAL2=VAL1*CONVFC                                                          
      GO TO 5000                                                                
C                                                                               
C                                                                               
C                                                                               
   50 CONTINUE                                                                  
      GO TO (100,200,300,400) N2                                                
C                                                                               
C                                                                               
C                                                                               
  100 CONTINUE                                                                  
      GO TO (110,120,130,140) N3                                                
  110 CONTINUE                                                                  
      VAL2=VAL1                                                                 
      GO TO 5000                                                                
  120 CONTINUE                                                                  
      VAL2=VAL1-273.15                                                          
      GO TO 5000                                                                
  130 CONTINUE                                                                  
      VAL2=9.0/5.0*VAL1                                                         
      GO TO 5000                                                                
  140 CONTINUE                                                                  
      VAL2=9.0/5.0*VAL1-459.67                                                  
      GO TO 5000                                                                
C                                                                               
C                                                                               
C                                                                               
  200 CONTINUE                                                                  
      GO TO (210,220,230,240) N3                                                
  210 CONTINUE                                                                  
      VAL2=VAL1+273.15                                                          
      GO TO 5000                                                                
  220 CONTINUE                                                                  
      VAL2=VAL1                                                                 
      GO TO 5000                                                                
  230 CONTINUE                                                                  
      VAL2=9.0/5.0*VAL1+491.67                                                  
      GO TO 5000                                                                
  240 CONTINUE                                                                  
      VAL2=9.0/5.0*VAL1+32.0                                                    
      GO TO 5000                                                                
C                                                                               
C                                                                               
C                                                                               
  300 CONTINUE                                                                  
      GO TO (310,320,330,340) N3                                                
  310 CONTINUE                                                                  
      VAL2=5.0/9.0*VAL1                                                         
      GO TO 5000                                                                
  320 CONTINUE                                                                  
      VAL2=5.0/9.0*(VAL1-491.67)                                                
      GO TO 5000                                                                
  330 CONTINUE                                                                  
      VAL2=VAL1                                                                 
      GO TO 5000                                                                
  340 CONTINUE                                                                  
      VAL2=VAL1-459.67                                                          
      GO TO 5000                                                                
C                                                                               
C                                                                               
C                                                                               
  400 CONTINUE                                                                  
      GO TO (410,420,430,440) N3                                                
  410 CONTINUE                                                                  
      VAL2=5.0/9.0*VAL1+255.372                                                 
      GO TO 5000                                                                
  420 CONTINUE                                                                  
      VAL2=5.0/9.0*(VAL1-32.0)                                                  
      GO TO 5000                                                                
  430 CONTINUE                                                                  
      VAL2=VAL1+459.67                                                          
      GO TO 5000                                                                
  440 CONTINUE                                                                  
      VAL2=VAL1                                                                 
C                                                                               
C                                                                               
C                                                                               
 5000 CONTINUE                                                                  
      RETURN                                                                    
      END                                                                       
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
C                                                                               
C       DISCHARGE RATE ( SATURATION PRESSURE )                                  
C                                                                               
C                                                                               
C       DIMENSION    ::                                                         
C                                                                               
C        DIACHARGE RATE   :  KILO-GRAM / ( SQUARE-METER SEC )                   
C                                                                               
C        PRESSURE         :  PASCAL                                             
C                                                                               
C                                                                               
C++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++         
C                                                                               
      COMPLEX FUNCTION SDCRP(P)                                               
C                                                                               
C                                                                               
C                                                                               
      COMPLEX*8 SATVP,SADEXP,CGM,CRO                                            
      REAL*4 IRO,IGM,ITEMP                                                      
C                                                                               
C                                                                               
C                                                                               
      CGM=SADEXP(P)                                                             
      CRO=SATVP(P)                                                              
C                                                                               
C                                                                               
C                                                                               
      RGM=REAL(CGM)                                                             
      IGM=AIMAG(CGM)                                                            
C                                                                               
C                                                                               
C                                                                               
      RRO=1.0/REAL(CRO)                                                         
      IRO=1.0/AIMAG(CRO)                                                        
C                                                                               
C                                                                               
C                                                                               
      RTEMP=RGM*(2.0/(RGM+1.0))**((RGM+1.0)/(RGM-1.0))                          
      ITEMP=IGM*(2.0/(IGM+1.0))**((IGM+1.0)/(IGM-1.0))                          
C                                                                               
C                                                                               
C                                                                               
      RTEMP=SQRT(RTEMP*P*RRO)                                                   
      ITEMP=SQRT(ITEMP*P*IRO)                                                   
C                                                                               
C                                                                               
C                                                                               
      SDCRP=CMPLX(RTEMP,ITEMP)                                                  
C                                                                               
C                                                                               
C                                                                               
      RETURN                                                                    
      END                                                                       
