      SUBROUTINE  DNLBIT(X,FUNCT,XL,XR,EPS,ITEND,IER)                           
      IMPLICIT      REAL*8( A-H,O-Z )                                           
      IER = 0                                                                   
      X   = XL                                                                  
      FL  = FUNCT(X)                                                            
      IF( FL ) 100,555,100                                                      
  100 X   = XR                                                                  
      FR  = FUNCT(X)                                                            
      IF( FR ) 110,555,110                                                      
  110 IF( FL*FR ) 130,555,120                                                   
  120 X   = (XL+XR)*.5D+0                                                       
      IER = 2                                                                   
      WRITE(6,620)                                                              
      RETURN                                                                    
C                                                                               
C                                                                               
  130 DO  250 N=1,ITEND                                                         
      X   = (XL+XR)*0.5D+0                                                      
      F   = FUNCT(X)                                                            
      IF( F ) 140,555,140                                                       
C                                                                               
  140 IF( F *FL ) 150,555,160                                                   
  150 XR  = X                                                                   
      FR  = F                                                                   
      GO TO 170                                                                 
  160 XL  = X                                                                   
      FL  = F                                                                   
  170 DF  = FL-FR                                                               
      DX  = (XR-XL)*FL                                                          
      DX  = DX/DF                                                               
      X   = XL+DX                                                               
      F   = FUNCT(X)                                                            
      IF( F ) 175,555,175                                                       
C                                                                               
  175 IF( FL*F ) 180,190,190                                                    
  180 XR  = X                                                                   
      FR  = F                                                                   
      GO TO 200                                                                 
  190 XL  = X                                                                   
      FL  = F                                                                   
  200 DX  = XR-XL                                                               
      IF( DABS(X).LE.1.0D0 ) GO TO 210                                          
      DX  = DX/X                                                                
  210 CONTINUE                                                                  
      IF( DABS(DX)-EPS) 220,220,250                                             
  220 X   = XR                                                                  
      IF( DABS(FR)-1.0D-12 ) 555,555,230                                        
  230 X   = XL                                                                  
      IF( DABS(FL)-1.0D-12 ) 555,555,250                                        
  250 CONTINUE                                                                  
      IER = 1                                                                   
      WRITE(6,610)                                                              
C                                                                               
  555 RETURN                                                                    
  610 FORMAT(1H0,'(SUBR. DNLBIT) REQUIRED ACCURACY IS NOT SATISFIED AT A        
     *PPOINTED TIMES.' )                                                        
  620 FORMAT(1H0,'(SUBR. DNLBIT) NO CALCULATION FOR F(XL).F(XR) > 0 .' )        
      END                                                                       

